INSERT INTO `tbl_product` (`id`, `category_id`, `name`, `content`, `descripition`, `product_hot`, `img`, `price`) VALUES
(60, 2, 'Samsung Galaxy S21 FE', '<p>Giảm đến 4,000,000đ khi Thu cũ Đổi mới (&Aacute;p dụng t&ugrave;y model m&aacute;y cũ)&nbsp;</p>\r\n\r\n<p>Ưu đ&atilde;i ph&ograve;ng chờ hạng thương gia&nbsp;</p>\r\n\r\n<p>Trả g&oacute;p 0% thẻ t&iacute;n dụng, chuyển đổi qua Qu&agrave; tặng Galaxy&nbsp;</p>', '<h3><a href=\"#\" target=\"_blank\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB)\">Samsung Galaxy S21 FE 5G (6GB/128GB)</a>&nbsp;được Samsung ra mắt với d&aacute;ng dấp thời thượng, cấu h&igrave;nh khỏe, chụp ảnh đẹp với bộ 3 camera chất lượng, thời lượng pin đủ d&ugrave;ng hằng ng&agrave;y v&agrave; c&ograve;n g&igrave; nữa? Mời bạn kh&aacute;m ph&aacute; qua nội dung sau ngay.</h3>\r\n\r\n<h3>Vẻ ngo&agrave;i sang trọng, m&agrave;u sắc thời trang</h3>\r\n\r\n<p>Galaxy S21 FE 5G&nbsp;thiết kế mỏng nhẹ với độ d&agrave;y&nbsp;<strong>7.9 mm</strong>, khối lượng chỉ&nbsp;<strong>177 gram</strong>, c&aacute;c g&oacute;c cạnh bo tr&ograve;n cho cảm gi&aacute;c h&agrave;i h&ograve;a, mềm mại, kết hợp c&aacute;c t&ocirc;ng m&agrave;u thời thượng gồm t&iacute;m, xanh l&aacute;, x&aacute;m v&agrave; trắng gi&uacute;p bạn dễ d&agrave;ng tạo n&ecirc;n phong c&aacute;ch ri&ecirc;ng đầy c&aacute; t&iacute;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040738.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Vẻ ngoài sang trọng, màu sắc thời trang\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040738.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Vẻ ngoài sang trọng, màu sắc thời trang\" /></a></p>\r\n\r\n<p>C&aacute;c chi tiết được ho&agrave;n thiện tinh tế, khung m&aacute;y bằng nh&ocirc;m kh&aacute; nhẹ v&agrave; bền chắc, mặt lưng l&agrave;m từ nhựa, chế t&aacute;c c&oacute; độ nh&aacute;m nhẹ, cầm b&aacute;m tay, khi sử dụng c&oacute; hơi d&iacute;nh mồ h&ocirc;i nhưng rất dễ l&agrave;m sạch.&nbsp;</p>\r\n\r\n<p>M&agrave;n h&igrave;nh&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại chính hãng tại Thế Giới Di Động\">điện thoại th&ocirc;ng minh</a>&nbsp;phủ&nbsp;k&iacute;nh cường lực&nbsp;<strong>Corning Gorilla Glass Victus</strong>&nbsp;s&aacute;ng&nbsp;b&oacute;ng, gần như an to&agrave;n khi rơi ở độ cao&nbsp;<strong>2m&nbsp;</strong>xuống (th&ocirc;ng tin từ h&atilde;ng sản xuất c&ocirc;ng bố), hạn chế trầy xước hiệu quả.</p>\r\n\r\n<p>Ở cạnh h&ocirc;ng m&aacute;y, c&oacute; c&aacute;c n&uacute;t nguồn, &acirc;m lượng bấm &ecirc;m &aacute;i, khe gắn sim ở cạnh dưới, nằm gần micro n&ecirc;n khi lắp sim bạn cần ch&uacute; &yacute; để tr&aacute;nh nhầm lẫn thao t&aacute;c.&nbsp;</p>\r\n\r\n<p>Chuyển qua mặt sau của m&aacute;y th&igrave; c&aacute;ch sắp xếp camera sau theo h&agrave;ng dọc giống với phi&ecirc;n bản&nbsp;S21, cụm camera l&agrave;m gồ l&ecirc;n, c&oacute; thể bị xước nếu đặt nằm tr&ecirc;n mặt b&agrave;n nhiều lần, n&ecirc;n để bảo vệ ống k&iacute;nh tốt hơn, bạn c&oacute; thể d&ugrave;ng th&ecirc;m ốp lưng, miếng d&aacute;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040740.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Mặt sau của máy thì cách sắp xếp camera sau theo hàng dọc\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040740.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Mặt sau của máy thì cách sắp xếp camera sau theo hàng dọc\" /></a></p>\r\n\r\n<p>Sản phẩm được thiết kế&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại có kháng nước, chống bụi tại Thế Giới Di Động\">kh&aacute;ng nước, kh&aacute;ng bụi</a>&nbsp;ti&ecirc;u chuẩn&nbsp;<strong>IP68</strong>, giảm khả năng bị nước, bụi bẩn x&acirc;m nhập g&acirc;y ảnh hưởng đến tuổi thọ v&agrave; hoạt động của m&aacute;y, cho bạn tự tin mang theo điện thoại trong c&aacute;c h&agrave;nh tr&igrave;nh thường nhật cũng như khi đi phượt, c&ocirc;ng t&aacute;c xa nh&agrave;.&nbsp;</p>\r\n\r\n<h3>Trải nghiệm xem ch&acirc;n thật</h3>\r\n\r\n<p>Với k&iacute;ch thước&nbsp;<strong>6.4 inch</strong>, đường viền kh&aacute; mỏng, camera trước dạng đục lỗ cho kh&ocirc;ng gian hiển thị rộng r&atilde;i, bạn c&oacute; thể xem được nhiều chi tiết tr&ecirc;n m&agrave;n h&igrave;nh hơn.</p>\r\n\r\n<p>Hỗ trợ c&ocirc;ng nghệ m&agrave;n h&igrave;nh hiện đại&nbsp;<strong>Dynamic AMOLED 2X</strong>,&nbsp;độ ph&acirc;n giả<strong>i Full HD+</strong>,&nbsp;tần số qu&eacute;t&nbsp;<strong>120 Hz</strong>, cho h&igrave;nh ảnh t&aacute;i hiện chi tiết, mượt m&agrave;, m&agrave;u sắc tươi tắn, độ tương phản cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040742.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Trải nghiệm xem chân thật\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040742.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Trải nghiệm xem chân thật\" /></a></p>\r\n\r\n<p>C&oacute; độ s&aacute;ng tối đa&nbsp;<strong>1200 nits&nbsp;</strong>xem trong nh&agrave; v&agrave; ngo&agrave;i trời đều cho chất lượng h&igrave;nh ảnh tốt. Điện thoại t&iacute;ch hợp loa k&eacute;p stereo trong trẻo cho &acirc;m lượng ở mức kh&aacute;.&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, nằm b&ecirc;n dưới của m&agrave;n h&igrave;nh c&oacute; cảm biến v&acirc;n tay quang học cho ph&eacute;p mở kh&oacute;a bằng v&acirc;n tay nhanh ch&oacute;ng, bạn cũng c&oacute; thể chọn mở kh&oacute;a bằng khu&ocirc;n mặt với camera trước t&ugrave;y theo từng ho&agrave;n cảnh sử dụng.</p>\r\n\r\n<h3>Hiệu năng ổn định, chơi game mượt m&agrave;</h3>\r\n\r\n<p>Cung cấp sức mạnh cho S21 FE l&agrave; CPU&nbsp;<strong>Exynos 2100</strong>&nbsp;c&oacute; sức mạnh được đ&aacute;nh gi&aacute; l&agrave; tương đương Snapdragon 888 n&ecirc;n chắc chắn sẽ kh&ocirc;ng l&agrave;m thất vọng bạn khi n&oacute; đ&aacute;p ứng được từ c&aacute;c t&aacute;c vụ hằng ng&agrave;y như đọc b&aacute;o, xem phim đến g&otilde; chữ trong Word, Excel mượt m&agrave;. Kiểm tra hiệu năng với phần mềm&nbsp;PCMark (b&ecirc;n tr&aacute;i) cho 14.234 điểm,&nbsp;GeekBench 5 (b&ecirc;n phải) cho 745 điểm đơn nh&acirc;n v&agrave; 2.183 điểm đa nh&acirc;n.</p>\r\n', 1, '6239d2cc398f5.jpg,6239d2cc3a8f3.jpg,6239d2cc3aa22.jpg', 18990000),
(61, 2, 'Samsung Galaxy S22', '<p>+ Giảm đến 4,000,000đ khi Thu cũ Đổi mới (&Aacute;p dụng t&ugrave;y model m&aacute;y cũ)&nbsp;</p>\r\n\r\n<p>+ Ưu đ&atilde;i ph&ograve;ng chờ hạng thương gia&nbsp;</p>\r\n\r\n<p>+ Trả g&oacute;p 0% thẻ t&iacute;n dụng, chuyển đổi qua Qu&agrave; tặng Galaxy</p>', '<h3>&nbsp;</h3>\r\n\r\n<h3><a href=\"#\" target=\"_blank\" title=\"Tham khảo thông tin sản phẩm tại Thế Giới Di Động\">Samsung Galaxy S22</a>&nbsp;ra mắt với diện mạo v&ocirc; c&ugrave;ng tinh tế v&agrave; trẻ trung, mang trong m&igrave;nh thiết kế phẳng theo xu hướng thịnh h&agrave;nh, m&agrave;n h&igrave;nh 120 Hz mượt m&agrave;, cụm camera AI 50 MP v&agrave; bộ xử l&yacute; đến từ Qualcomm.</h3>\r\n\r\n<h3>Thiết kế thu h&uacute;t mọi &aacute;nh nh&igrave;n&nbsp;</h3>\r\n\r\n<p>Cảm gi&aacute;c đầu ti&ecirc;n Samsung Galaxy S22 mang lại cho m&igrave;nh khi sử dụng l&agrave; m&aacute;y cho cảm gi&aacute;c cầm nắm cực kỳ chắc chắn v&agrave; đầm tay với thiết kế nhỏ gọn của m&igrave;nh.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; một chiếc&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;v&ocirc; c&ugrave;ng bền bỉ khi khung viền được cấu th&agrave;nh từ Armor Aluminum cứng c&aacute;p, hỗ trợ kh&aacute;ng bụi, nước chuẩn IP68 v&agrave; đ&acirc;y cũng l&agrave; một trong những sản phẩm được trang bị k&iacute;nh cường lực Corning&nbsp;Gorilla Glass Victus+ đầu ti&ecirc;n tr&ecirc;n thị trường.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/231110/samsung-galaxy-s22-1-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế thu hút - Samsung Galaxy S22\" src=\"https://cdn.tgdd.vn/Products/Images/42/231110/samsung-galaxy-s22-1-1.jpg\" title=\"Thiết kế thu hút - Samsung Galaxy S22\" /></a></p>\r\n\r\n<p>Mặt lưng cũng được ho&agrave;n thiện từ k&iacute;nh với thiết kế phẳng, viền m&agrave;n h&igrave;nh của Galaxy S22 được l&agrave;m mỏng ở cả 4 cạnh tr&ocirc;ng rất c&acirc;n đối v&agrave; gọn g&agrave;ng. Tất cả những điều tr&ecirc;n tạo n&ecirc;n một cảm gi&aacute;c cầm nắm, sử dụng rất tốt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/231110/samsung-galaxy-s22-2-1.jpg\" onclick=\"return false;\"><img alt=\"Mạnh mẽ và sang trọng - Samsung Galaxy S22\" src=\"https://cdn.tgdd.vn/Products/Images/42/231110/samsung-galaxy-s22-2-1.jpg\" title=\"Mạnh mẽ và sang trọng - Samsung Galaxy S22\" /></a></p>\r\n\r\n<p>Cụm camera ph&iacute;a sau c&oacute; lồi hơn mặt lưng một ch&uacute;t, c&oacute; m&agrave;u sắc tương đồng với m&agrave;u sản phẩm&nbsp;tương tự với d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s21\" target=\"_blank\" title=\"Tham khảo thông tin sản phẩm tại Thế Giới Di Động\">Galaxy S21</a>&nbsp;đ&atilde; ra mắt trước đ&oacute;. Nh&igrave;n chung phần thiết kế m&igrave;nh thấy rằng Galaxy S22 kh&ocirc;ng c&oacute; sự thay đổi qu&aacute; nhiều khi so với thế hệ S21, tuy nhi&ecirc;n theo c&aacute; nh&acirc;n m&igrave;nh S22 đ&atilde; được tối ưu v&agrave; gọn g&agrave;ng hơn thế hệ trước.</p>\r\n\r\n<h3>Trải nghiệm thị gi&aacute;c được n&acirc;ng tầm</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh 6.1 inch cực kỳ nhỏ gọn, độ ph&acirc;n giải Full HD+ c&ugrave;ng tấm nền Dynamic AMOLED 2X cho khả năng hiển thị v&ocirc; c&ugrave;ng chi tiết, m&agrave;u đen s&acirc;u, m&agrave;u sắc h&agrave;i h&ograve;a, hỗ trợ dải m&agrave;u rộng DCI-P3 gi&uacute;p cho ch&uacute;ng ta c&oacute; trải nghiệm sử dụng l&agrave;m việc, giải tr&iacute; một c&aacute;ch sinh động hơn.&nbsp;</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"#\" target=\"_blank\" title=\"DCI-P3 là gì?\">Dải m&agrave;u rộng DCI-P3 l&agrave; g&igrave;?</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/231110/samsung-galaxy-s22-3-1.jpg\" onclick=\"return false;\"><img alt=\"Hiển thị màu sắc tuyệt vời - Samsung Galaxy S22\" src=\"https://cdn.tgdd.vn/Products/Images/42/231110/samsung-galaxy-s22-3-1.jpg\" title=\"Hiển thị màu sắc tuyệt vời - Samsung Galaxy S22\" /></a></p>\r\n\r\n<p>Với tần số qu&eacute;t 120 Hz m&aacute;y cho trải nghiệm vuốt chạm cực k&igrave; mượt m&agrave;, hiệu ứng chuyển cảnh hay hiệu ứng khi mở ứng dụng rất l&agrave; trơn tru v&agrave; hầu như kh&ocirc;ng c&oacute; hiện tượng khựng.</p>\r\n\r\n<p>Galaxy S22 c&oacute; độ s&aacute;ng tối đa đến 1300 nits cho hiển thị nội dung tốt cho d&ugrave; bạn xem ở ngo&agrave;i trời nắng ch&oacute;i. Chế độ bảo vệ mắt được trang bị gi&uacute;p đ&ocirc;i mắt hạn chế tiếp x&uacute;c với &aacute;nh s&aacute;ng xanh cho qu&aacute; tr&igrave;nh sử dụng điện thoại của bạn trở n&ecirc;n thoải m&aacute;i hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/231110/samsung-galaxy-s22-4-1.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay dưới màn hình - Samsung Galaxy S22\" src=\"https://cdn.tgdd.vn/Products/Images/42/231110/samsung-galaxy-s22-4-1.jpg\" title=\"Cảm biến vân tay dưới màn hình - Samsung Galaxy S22\" /></a></p>\r\n\r\n<h3>&nbsp;</h3>\r\n', 1, '6239d22279b60.jpg,6239d22279cdc.jpg,6239d22279e07.jpg', 21990000),
(62, 2, ' Samsung Galaxy A03', '<p>Giảm đến 4,000,000đ khi Thu cũ Đổi mới (&Aacute;p dụng t&ugrave;y model m&aacute;y cũ)&nbsp;</p>\r\n\r\n<p>Ưu đ&atilde;i ph&ograve;ng chờ hạng thương gia&nbsp;</p>\r\n\r\n<p>Trả g&oacute;p 0% thẻ t&iacute;n dụng, chuyển đổi qua Qu&agrave; tặng Galaxy&nbsp;</p>', '<h3>&nbsp;</h3>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>Samsung Galaxy A03&nbsp;l&agrave; chiếc&nbsp;điện thoại Galaxy A đầu ti&ecirc;n của nh&agrave;&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo sản phẩm Samsung kinh doanh tại Thế Giới Di Động\">Samsung</a>&nbsp;trong năm 2022 tại thị trường Việt Nam. Đ&acirc;y l&agrave; sản phẩm c&oacute; mức gi&aacute; dễ tiếp cận, c&oacute; camera ch&iacute;nh đến 48 MP, pin 5000 mAh thoải m&aacute;i sử dụng cả ng&agrave;y.</h3>\r\n\r\n<h3>Thiết kế v&acirc;n đan ch&eacute;o</h3>\r\n\r\n<p>Galaxy A03 c&oacute; thiết kế nhựa nguy&ecirc;n khối, mặt lưng nh&aacute;m, c&oacute; c&aacute;c đường v&acirc;n đan ch&eacute;o nhau một c&aacute;ch nhẹ nh&agrave;ng mang lại độ nh&aacute;m nhất định khi cầm nắm, cảm gi&aacute;c đầm tay v&agrave; c&oacute; thể hạn chế b&aacute;m mồ h&ocirc;i, dấu v&acirc;n tay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/251856/samsung-galaxy-a03-1-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế vân đan chéo | Samsung Galaxy A03\" src=\"https://cdn.tgdd.vn/Products/Images/42/251856/samsung-galaxy-a03-1-1.jpg\" title=\"Thiết kế vân đan chéo | Samsung Galaxy A03\" /></a></p>\r\n\r\n<p>Phần khung viền được v&aacute;t cong, &ocirc;m về ph&iacute;a trước n&ecirc;n cho cảm gi&aacute;c m&aacute;y mỏng hơn, tạo cho m&igrave;nh cảm gi&aacute;c cầm nắm rất l&agrave; thoải m&aacute;i cộng th&ecirc;m với việc đặt vị tr&iacute; ph&iacute;m tăng giảm &acirc;m lượng, ph&iacute;m nguồn hợp l&yacute; th&igrave; khi cầm sản phẩm một thời gian d&agrave;i m&igrave;nh kh&ocirc;ng gặp một bất tiện n&agrave;o.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/251856/samsung-galaxy-a03-2-1.jpg\" onclick=\"return false;\"><img alt=\"Cạnh bên | Samsung Galaxy A03\" src=\"https://cdn.tgdd.vn/Products/Images/42/251856/samsung-galaxy-a03-2-1.jpg\" title=\"Cạnh bên | Samsung Galaxy A03\" /></a></p>\r\n\r\n<p>V&agrave; tầm gi&aacute; n&agrave;y Galaxy A03 chỉ được trang bị cổng sạc Micro USB v&agrave; k&egrave;m theo đ&oacute; l&agrave; jack 3.5 mm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/251856/samsung-galaxy-a03-3.jpg\" onclick=\"return false;\"><img alt=\"Cạnh dưới | Samsung Galaxy A03\" src=\"https://cdn.tgdd.vn/Products/Images/42/251856/samsung-galaxy-a03-3.jpg\" title=\"Cạnh dưới | Samsung Galaxy A03\" /></a></p>\r\n\r\n<h3>Hiệu năng ổn định với chip Unisoc</h3>\r\n\r\n<p>Galaxy A03 sử dụng bộ vi xử l&yacute; Unisoc T606 8 nh&acirc;n nghe kh&aacute; l&agrave; mới lạ, hiệu năng n&agrave;y đ&aacute;p ứng ổn c&aacute;c nhu cầu, t&aacute;c vụ cơ bản như l&agrave; lướt web, nghe gọi, nhắn tin, đọc b&aacute;o h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/251856/samsung-galaxy-a03-4.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng ổn định | Samsung Galaxy A03\" src=\"https://cdn.tgdd.vn/Products/Images/42/251856/samsung-galaxy-a03-4.jpg\" title=\"Hiệu năng ổn định | Samsung Galaxy A03\" /></a></p>\r\n\r\n<p>M&aacute;y c&oacute;&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại RAM 3 GB tại Thế Giới Di Động\">RAM 3 GB</a>&nbsp;v&agrave;&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại bộ nhớ trong 32 GB tại Thế Giới Di Động\">bộ nhớ trong 32GB</a>, một con số kh&aacute; khi&ecirc;m tốn nhưng m&igrave;nh nghĩ với tầm gi&aacute; n&agrave;y th&igrave; mức trang bị n&agrave;y cũng kh&aacute; hợp l&yacute;. Để sử dụng mượt m&agrave; hơn c&oacute; thể giải ph&oacute;ng dung lượng RAM thường xuy&ecirc;n để c&oacute; một c&aacute;i trải nghiệm tốt hơn. Ngo&agrave;i ra bạn c&oacute; thể chọn phi&ecirc;n bản&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo thông tin sản phẩm tại Thế Giới Di Động\">Galaxy A03 4GB</a>&nbsp;để c&oacute; RAM v&agrave; bộ nhớ trong lớn hơn.</p>\r\n\r\n<p>M&igrave;nh c&oacute; chơi tựa game Li&ecirc;n Qu&acirc;n Mobile phổ biến hiện nay, với t&aacute;c vụ n&agrave;y đ&ocirc;i l&uacute;c thao t&aacute;c di chuyển c&oacute; độ trễ tuy nhi&ecirc;n kh&ocirc;ng đ&aacute;ng kể, m&agrave;u sắc ổn nhưng chi tiết hiển thị đồ họa đạt ở mức kh&aacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/251856/samsung-galaxy-a03-5.jpg\" onclick=\"return false;\"><img alt=\"Chơi game liên quân | Samsung Galaxy A03\" src=\"https://cdn.tgdd.vn/Products/Images/42/251856/samsung-galaxy-a03-5.jpg\" title=\"Chơi game liên quân | Samsung Galaxy A03\" /></a></p>\r\n\r\n<p>M&aacute;y c&oacute; thể bật 60 FPS &iacute;t xảy ra hiện tượng drop v&agrave; giật lag. M&igrave;nh kh&aacute; l&agrave; bất ngờ với điều n&agrave;y khi trải nghiệm tr&ecirc;n một con m&aacute;y trong ph&acirc;n kh&uacute;c gi&aacute; rẻ. Sau khi chơi 1, 2 trận m&igrave;nh thấy m&aacute;y chỉ ấm nhẹ ở mặt lưng.</p>\r\n\r\n<p>Nh&igrave;n chung, về c&aacute;c t&aacute;c vụ nhẹ h&agrave;ng ng&agrave;y th&igrave; Galaxy A03 vẫn đ&aacute;p ứng vừa đủ nhu cầu của m&igrave;nh, nhưng c&ograve;n chơi game nặng thường xuy&ecirc;n th&igrave; m&igrave;nh nghĩ đ&acirc;y chưa phải l&agrave; một sự lựa chọn ho&agrave;n hảo.</p>\r\n\r\n<h3>Kh&ocirc;ng gian giải tr&iacute; lớn</h3>\r\n\r\n<p>Galaxy A03 sử dụng m&agrave;n h&igrave;nh giọt nước Infinity-V quen thuộc, c&oacute; k&iacute;ch thước 6.5 inch, độ ph&acirc;n giải HD+ tr&ecirc;n tấm nền IPS. Phần m&agrave;n h&igrave;nh n&agrave;y cho m&igrave;nh một kh&ocirc;ng gian giải tr&iacute; rộng r&atilde;i, c&oacute; thể hiển thị nhiều th&ocirc;ng tin hơn khi m&agrave; m&igrave;nh lướt Facebook, lướt web hay xem phim.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/251856/samsung-galaxy-a03-6.jpg\" onclick=\"return false;\"><img alt=\"Màn hình giọt nước | Samsung Galaxy A03\" src=\"https://cdn.tgdd.vn/Products/Images/42/251856/samsung-galaxy-a03-6.jpg\" title=\"Màn hình giọt nước | Samsung Galaxy A03\" /></a></p>\r\n\r\n<p>M&agrave;u sắc hiển thị tương đối tươi, g&oacute;c nh&igrave;n tạm, độ s&aacute;ng của m&aacute;y kh&ocirc;ng qu&aacute; cao vẫn c&oacute; thể sử dụng trong điều kiện ngo&agrave;i trời.</p>\r\n\r\n<p>Ngo&agrave;i ra, chiếc điện thoại n&agrave;y c&ograve;n hỗ trợ c&ocirc;ng nghệ &acirc;m thanh Dolby Atmos, cho bạn trải nghiệm &acirc;m thanh một c&aacute;ch trọn vẹn v&agrave; tốt hơn, n&acirc;ng cao khả năng giải tr&iacute; của m&aacute;y, tuy nhi&ecirc;n c&ocirc;ng nghệ n&agrave;y chỉ hoạt động khi kết nối với tai nghe hỗ trợ &acirc;m thanh nổi hoặc loa.</p>\r\n', 0, '6239d1ba10f8a.jpg,6239d1ba1310b.jpg,6239d1ba13281.jpg', 7900000),
(63, 88, 'iPhone 11', '<p>Chọn 1 trong c&aacute;c khuyến m&atilde;i:</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;+ Giảm gi&aacute; 1,800,000đ<em>*</em></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;+ Phiếu mua h&agrave;ng AVA 2,700,000đ<em>*</em></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;+ Phiếu mua h&agrave;ng B&amp;a', '<h3>Apple đ&atilde; ch&iacute;nh thức tr&igrave;nh l&agrave;ng bộ 3 si&ecirc;u phẩm iPhone 11, trong đ&oacute; phi&ecirc;n bản&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo thông tin sản phẩm tại TopZone.vn\">iPhone 11 64GB</a>&nbsp;c&oacute; mức gi&aacute; rẻ nhất nhưng vẫn được n&acirc;ng cấp mạnh mẽ như&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone Xr chính hãng\">iPhone Xr</a>&nbsp;ra mắt&nbsp;trước đ&oacute;.</h3>\r\n\r\n<h3>N&acirc;ng cấp mạnh mẽ về camera</h3>\r\n\r\n<p>N&oacute;i về n&acirc;ng cấp th&igrave; camera ch&iacute;nh l&agrave; điểm c&oacute; nhiều cải tiến nhất tr&ecirc;n thế hệ&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone iPhone chính hãng\">iPhone</a>&nbsp;mới.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/153856/iphone-11-tgdd42.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Thiết kế nhiều màu sắc\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd42.jpg\" title=\"Điện thoại iPhone 11 64GB | Thiết kế nhiều màu sắc\" /></a></p>\r\n\r\n<p>Nếu như trước đ&acirc;y iPhone Xr chỉ c&oacute; một camera th&igrave; nay với&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 11 đang kinh doanh tại TopZone thương hiệu của Thế Giới Di Động\">iPhone 11</a>&nbsp;ch&uacute;ng ta sẽ c&oacute; tới hai camera ở mặt sau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/153856/iphone-114-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-114-1.jpg\" title=\"Điện thoại iPhone 11 64GB | Camera sau\" /></a></p>\r\n\r\n<p>Ngo&agrave;i camera ch&iacute;nh vẫn c&oacute; độ ph&acirc;n giải 12 MP th&igrave; ch&uacute;ng ta sẽ c&oacute; th&ecirc;m một camera g&oacute;c si&ecirc;u rộng v&agrave; cũng với độ ph&acirc;n giải tương tự.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/153856/iphone-116.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ chân dung\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-116.jpg\" title=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ chân dung\" /></a></p>\r\n\r\n<p><em>Ảnh chụp ch&acirc;n dung với iPhone 11</em></p>\r\n\r\n<p>Theo Apple th&igrave; việc chuyển đổi qua lại giữa hai ống k&iacute;nh th&igrave; sẽ kh&ocirc;ng l&agrave;m thay đổi m&agrave;u sắc của bức ảnh.</p>\r\n', 1, '6239d14424ee9.jpg,6239d14426439.jpg,6239d1442658e.jpg', 16990000),
(64, 88, ' iPhone SE', '<p>Chọn 1 trong c&aacute;c khuyến m&atilde;i:</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;+ Giảm gi&aacute; 1,800,000đ<em>*</em></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;+ Phiếu mua h&agrave;ng AVA 2,700,000đ<em>*</em></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;+ Phiếu mua h&agrave;ng B&amp;a', '<h3><a href=\"#\" target=\"_blank\" title=\"Tham khảo giá iPhone SE 2020 128 GB chính hãng tại Thegioididong.com\" type=\"Tham khảo giá iPhone SE 2020 128 GB chính hãng tại Thegioididong.com\">iPhone SE 2020</a>&nbsp;khi được cho ra mắt đ&atilde; l&agrave;m thỏa m&atilde;n triệu t&iacute;n đồ T&aacute;o khuyết. M&aacute;y sở hữu thiết kế si&ecirc;u nhỏ gọn như&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo giá điện thoại iPhone 8 chính hãng tại Thegioididong.com\" type=\"Tham khảo giá điện thoại iPhone 8 chính hãng tại Thegioididong.com\">iPhone 8</a>, chip A13 Bionic cho hiệu năng khủng như&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo giá điện thoại iPhone 11 chính hãng tại Thegioididong.com\" type=\"Tham khảo giá điện thoại iPhone 11 chính hãng tại Thegioididong.com\">iPhone 11</a>, nhưng iPhone SE 2020 lại c&oacute; một mức gi&aacute; tốt đến bất ngờ.</h3>\r\n\r\n<h3>Thiết kế nhỏ gọn trong l&ograve;ng b&agrave;n tay.</h3>\r\n\r\n<p>Kh&ocirc;ng nằm ngo&agrave;i mọi đồn đo&aacute;n, iPhone SE 2020 mang tr&ecirc;n m&igrave;nh h&igrave;nh d&aacute;ng của iPhone 8 c&aacute;ch đ&acirc;y 3 năm, vẫn mặt lưng bằng k&iacute;nh v&agrave; khung kim loại,&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại có hỗ trợ mở khóa cảm biến vân tay tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại có hỗ trợ mở khóa cảm biến vân tay tại Thegioididong.com\">cảm biến v&acirc;n tay</a>&nbsp;touch ID huyền thoại đ&atilde; quay trở lại v&agrave; nằm ở vị tr&iacute; quen thuộc.</p>\r\n\r\n<p>Đối với những ai ưa th&iacute;ch sự nhỏ gọn, đặc biệt l&agrave; c&aacute;c bạn nữ th&igrave; iPhone SE l&agrave; một sự lựa chọn l&yacute; tưởng. K&iacute;ch thước nhỏ gọn kh&ocirc;ng qu&aacute; to, gi&uacute;p cho iPhone SE 2020 dễ d&agrave;ng cầm nắm v&agrave; sử dụng bằng 1 tay hay nh&eacute;t v&agrave;o t&uacute;i quần dễ d&agrave;ng.</p>\r\n\r\n<p>Giống với iPhone 8 đến 99%, nhưng bạn vẫn c&oacute; thể ph&acirc;n biệt 2 model n&agrave;y th&ocirc;ng qua logo t&aacute;o khuyết ở mặt lưng, nếu ở thiết bị tiền nhiệm tr&aacute;i t&aacute;o đặt hơi lệch ở ph&iacute;a tr&ecirc;n th&igrave; đến iPhone SE 2020 biểu tượng n&agrave;y đ&atilde; dời ngay ch&iacute;nh giữa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/222629/iphone-se-128gb-20201.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Apple iPhone SE 2020 | Thiết kế nhỏ gọn đi ngược xu hướng \" src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-20201.jpg\" title=\"Điện thoại Apple iPhone SE 2020 | Thiết kế nhỏ gọn đi ngược xu hướng \" /></a></p>\r\n\r\n<p>Cũng giống như c&aacute;c thế hệ iPhone gần đ&acirc;y, iPhone SE được trang bị khả năng&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại chống nước chống bụi tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại chống nước chống bụi tại Thegioididong.com\">kh&aacute;ng nước bụi</a>&nbsp;IP67 c&ugrave;ng t&iacute;nh năng&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại sạc không dây tại Thegioididong.com\">sạc kh&ocirc;ng d&acirc;y</a>&nbsp;kh&aacute; hữu &iacute;ch trong nhiều điều kiện sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/222629/iphone-se-128gb-2020-205920-125935.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Apple iPhone SE 2020 | Chống nước, kháng bụi IP67\" src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-2020-205920-125935.jpg\" title=\"Điện thoại Apple iPhone SE 2020 | Chống nước, kháng bụi IP67\" /></a></p>\r\n\r\n<p>Ở mặt trước, m&aacute;y trang bị một m&agrave;n h&igrave;nh Retina 4.7 inch, tấm nền IPS cho m&agrave;u sắc ch&iacute;nh x&aacute;c, tươi tắn ở nhiều g&oacute;c độ, d&ugrave; độ ph&acirc;n giải chỉ đạt HD nhưng vẫn cho ra h&igrave;nh ảnh r&otilde; n&eacute;t với chi tiết mịn m&agrave;ng m&agrave; kh&ocirc;ng giảm đi chất lượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/222629/iphone-se-128gb-20206.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Apple iPhone SE 2020 | Chất lượng hiển thị rõ nét trung thực dù độ phân giải chỉ HD\" src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-20206.jpg\" title=\"Điện thoại Apple iPhone SE 2020 | Chất lượng hiển thị rõ nét trung thực dù độ phân giải chỉ HD\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh &ldquo;kh&ocirc;ng đối thủ&rdquo; trong tầm gi&aacute;</h3>\r\n\r\n<p>Tr&aacute;i ngược với vẻ ngo&agrave;i nhỏ gọn, th&igrave; trang bị phần cứng b&ecirc;n trong iPhone SE 2020 cực kỳ mạnh mẽ với chip A13 Bionic cho hiệu năng v&ocirc; c&ugrave;ng ấn tượng. Với tầm gi&aacute; n&agrave;y, kh&oacute; một chiếc&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại Android chính hãng, giá tốt tại Thegioididong.com\" type=\"Tham khảo điện thoại Android chính hãng, giá tốt tại Thegioididong.com\">điện thoại Android</a>&nbsp;hiện nay c&oacute; thể đ&aacute;nh bại.</p>\r\n\r\n<p>Sở hữu vi xử l&yacute; c&oacute; tr&ecirc;n bộ 3 iPhone 11,&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 11 Pro chính hãng tại Thegioididong.com\" type=\"Tham khảo điện thoại iPhone 11 Pro chính hãng tại Thegioididong.com\">iPhone 11 Pro</a>&nbsp;v&agrave;&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo giá điện thoại iPhone 11 Pro Max chính hãng tại Thegioididong.com\" type=\"Tham khảo giá điện thoại iPhone 11 Pro Max chính hãng tại Thegioididong.com\">iPhone 11 Pro Max</a>, gi&uacute;p cho iPhone SE 2020 c&oacute; một tốc độ xử l&yacute; kinh ngạc từ những t&aacute;c vụ nhẹ nh&agrave;ng h&agrave;ng ng&agrave;y cho đến t&aacute;c vụ nặng chơi game hay thậm ch&iacute; chỉnh sửa đồ họa đều mượt m&agrave; nhanh ch&oacute;ng.</p>\r\n\r\n<p>Sau khi đo bằng ứng dụng AnTuTu th&igrave; điểm nhận về l&agrave; 364.049, ở mức n&agrave;y th&igrave; chỉnh sửa h&igrave;nh ảnh bằng Light Room Mobile cũng mượt nữa chứ kh&ocirc;ng kể g&igrave; đến việc chơi game nhẹ Subway Surfers hay Temple Run,...</p>\r\n', 0, '6239d1001da2f.jpeg,6239d1001db9b.jpeg,6239d1001dd12.jpeg', 14090000),
(65, 88, 'iPhone 13 Pro', '<p>Chọn 1 trong c&aacute;c khuyến m&atilde;i:</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;+ Giảm gi&aacute; 1,800,000đ<em>*</em></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;+ Phiếu mua h&agrave;ng AVA 2,700,000đ<em>*</em></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;+ Phiếu mua h&agrave;ng B&amp;a', '<h3>Mỗi lần ra mắt phi&ecirc;n bản mới l&agrave; mỗi lần&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone tại Thegioididong.com\">iPhone</a>&nbsp;chiếm s&oacute;ng tr&ecirc;n khắp c&aacute;c mặt trận v&agrave; lần n&agrave;y c&aacute;i t&ecirc;n khiến v&ocirc; số người &quot;sục s&ocirc;i&quot; l&agrave;&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo giá điện thoại iPhone 13 Pro chính hãng\">iPhone 13 Pro</a>, chiếc&nbsp;<a href=\"#\" target=\"_blank\" title=\"Điện thoại thông minh đang bán tại Thegioididong.com\">điện thoại th&ocirc;ng minh</a>&nbsp;vẫn giữ nguy&ecirc;n thiết kế cao cấp, cụm 3 camera được n&acirc;ng cấp, cấu h&igrave;nh mạnh mẽ c&ugrave;ng thời lượng pin lớn ấn tượng.&nbsp;</h3>\r\n\r\n<h3>Thiết kế đặc trưng với m&agrave;u sắc thời thượng</h3>\r\n\r\n<p>iPhone 13 Pro kh&ocirc;ng c&oacute; nhiều sự thay đổi về thiết kế, khi m&aacute;y vẫn sở hữu kiểu d&aacute;ng tương tự như&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo giá điện thoại iPhone 12 Pro chính hãng\">iPhone 12 Pro</a>&nbsp;với c&aacute;c cạnh viền vu&ocirc;ng vắn v&agrave; hai mặt k&iacute;nh cường lực cao cấp. Sở hữu 4 phi&ecirc;n bản m&agrave;u gồm xanh dương, bạc, v&agrave;ng đồng v&agrave; x&aacute;m cho bạn t&ugrave;y chọn theo sở th&iacute;ch của m&igrave;nh.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/230521/iphone-13-pro-1.jpg\" onclick=\"return false;\"><img alt=\"Sở hữu 4 phiên bản màu sắc trẻ trung - iPhone 13 Pro 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-1.jpg\" title=\"Sở hữu 4 phiên bản màu sắc trẻ trung - iPhone 13 Pro 128GB\" /></a></p>\r\n\r\n<p>M&aacute;y đạt ti&ecirc;u chuẩn&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo điện thoại chống nước bụi tại Thế Giới Di Động\">kh&aacute;ng nước và bụi</a>&nbsp;IP68 c&oacute; khả năng chống lại một số hạt bụi, v&agrave; được bảo vệ khi rơi xuống nước ở độ s&acirc;u đến 6 m&eacute;t trong 30 ph&uacute;t&nbsp;theo chuẩn IEC 60529, thoải m&aacute;i nhắn tin khi lỡ ra ngo&agrave;i gặp mưa, chụp ảnh tự tin khi đi hồ bơi, b&atilde;i biển,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/230521/iphone-13-pro-2.jpg\" onclick=\"return false;\"><img alt=\"Khả năng kháng bụi, nước - iPhone 13 Pro 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-2.jpg\" title=\"Khả năng kháng bụi, nước - iPhone 13 Pro 128GB\" /></a></p>\r\n\r\n<h3>N&acirc;ng cấp m&agrave;n h&igrave;nh với tần số qu&eacute;t 120 Hz</h3>\r\n\r\n<p>Chiếc iPhone 13 Pro vẫn được trang bị m&agrave;n h&igrave;nh k&iacute;ch thước 6.1 inch, với phần tai thỏ được l&agrave;m nhỏ hơn gi&uacute;p hiển thị th&ecirc;m nhiều th&ocirc;ng tin cũng như tăng cường cảm gi&aacute;c trải nghiệm. Bề mặt c&oacute; lớp phủ oleophobic chống b&aacute;m v&acirc;n tay, giữ cho m&agrave;n h&igrave;nh lu&ocirc;n được sạch mới.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/230521/iphone-13-pro-man-hinh.jpg\" onclick=\"return false;\"><img alt=\"Trang bị màn hình 6.1 inch - iPhone 13 Pro 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-man-hinh.jpg\" title=\"Trang bị màn hình 6.1 inch - iPhone 13 Pro 128GB\" /></a></p>\r\n\r\n<p>iPhone 13 Pro đ&atilde; được n&acirc;ng cấp l&ecirc;n tần số qu&eacute;t 120 Hz, với ProMotion c&oacute; thể l&agrave;m mới từ 10 đến 120 khung h&igrave;nh/gi&acirc;y. Với tần số qu&eacute;t cao, mọi thao t&aacute;c chuyển cảnh khi lướt ng&oacute;n tay tr&ecirc;n m&agrave;n h&igrave;nh trở n&ecirc;n mượt m&agrave; hơn đồng thời hiệu ứng thị gi&aacute;c khi ch&uacute;ng ta d&ugrave;ng&nbsp;<a href=\"#\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại chơi game tại Thế Giới Di Động\">điện thoại chơi game</a>&nbsp;hoặc xem video cũng cực kỳ m&atilde;n nh&atilde;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/230521/iphone-13-pro-5.jpg\" onclick=\"return false;\"><img alt=\"Chuyển động mượt mà nhờ tần số 120 Hz - iPhone 13 Pro 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-5.jpg\" title=\"Chuyển động mượt mà nhờ tần số 120 Hz - iPhone 13 Pro 128GB\" /></a></p>\r\n\r\n<p>Tốc độ l&agrave;m tươi tự động tăng l&ecirc;n để hiển thị hiệu suất đồ họa, tự động giảm xuống khi kh&ocirc;ng sử dụng để tiết kiệm năng lượng v&agrave; tốc độ l&agrave;m mới cũng được tự tinh chỉnh linh động để ph&ugrave; hợp hơn tốc độ lướt của ng&oacute;n tay bạn.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/230521/iphone-13-pro-4.jpg\" onclick=\"return false;\"><img alt=\"Tốc độ làm tươi được tự động hóa - iPhone 13 Pro 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/230521/iphone-13-pro-4.jpg\" title=\"Tốc độ làm tươi được tự động hóa - iPhone 13 Pro 128GB\" /></a></p>\r\n\r\n<p>Sử dụng tấm nền OLED c&oacute; số điểm ảnh l&ecirc;n đến 1170 x 2532 Pixels, độ s&aacute;ng điển h&igrave;nh 1000 nits, khi hiển thị video HDR 1200 nits, tăng l&ecirc;n 25% khi đặt dưới &aacute;nh mặt trời, sắc đen hiển thị s&acirc;u, sắc s&aacute;ng tươi tắn, khung h&igrave;nh sinh động như thực.&nbsp;</p>\r\n', 0, '6239d08ecbbdd.jpg,6239d08ecbd1e.jpg,6239d08ecbe2f.jpg', 30890000),
(133, 88, 'iPhone  X', '<p>Chọn 1 trong c&aacute;c khuyến m&atilde;i:</p>\r\n<p>+ Giảm giá 1,800,000đ*</p>\r\n<p>+ Phiếu mua hàng AVA 2,700,000đ*</p>\r\n<p>+ Phiến trị giá 500.000đ</p>\r\n\r\n', '<h2><strong>Những ưu điểm của iPhone X quốc tế 64GB</strong></h2>\r\n\r\n<h3><strong>1. iPhone X quốc tế c&oacute; thiết kế đột ph&aacute;</strong></h3>\r\n\r\n<p>Để kỷ niệm phi&ecirc;n bản iPhone thứ 10, Apple đ&atilde; cho ra mắt iPhone X với những cải tiến nổi bật trong thiết kế. Những đặc điểm nổi bật cần kể đến l&agrave;:</p>\r\n\r\n<ul>\r\n	<li>Thiết kế m&agrave;n h&igrave;nh tai thỏ đột ph&aacute; so với những phi&ecirc;n bản tiền nhiệm.</li>\r\n	<li>M&agrave;n h&igrave;nh kh&ocirc;ng viền với k&iacute;ch thước l&ecirc;n đến 5.8 inch.</li>\r\n	<li>C&ocirc;ng nghệ OLED với độ ph&acirc;n giải 2,436 x 1,125 pixel v&agrave; mật độ điểm ảnh 458 ppi.</li>\r\n	<li>N&uacute;t Home,Touch ID v&agrave; cổng tai nghe 3.5mm được loại bỏ.</li>\r\n	<li>N&uacute;t trợ l&yacute; ảo Siri được t&iacute;ch hợp mang đến trải nghiệm mới mẻ.</li>\r\n	<li>Viền kim loại b&oacute;ng bẩy v&agrave; mặt lưng ốp k&iacute;nh sang trọng khẳng định vị thế của một chiếc điện thoại cao cấp.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Iphone X 64Gb Quốc Tế Cũ 2\" class=\"aligncenter size-full wp-image-23983\" src=\"https://galaxydidong.vn/wp-content/uploads/2017/11/iphone-x-64gb-quoc-te-cu-2.png\" style=\"height:450px; width:800px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>iPhone X sở hữu thiết kế đột ph&aacute;</em></p>\r\n\r\n<p>Những sự thay đổi n&agrave;y mang đến cho người d&ugrave;ng những trải nghiệm mới mẻ v&agrave; tiện &iacute;ch hiện đại. iPhone X xứng đ&aacute;ng l&agrave; một trong những mẫu điện thoại tốt nhất hiện nay.</p>\r\n\r\n<h3><strong>2. Hiệu năng </strong><strong>iPhone X quốc tế </strong><strong>v&ocirc; đối</strong></h3>\r\n\r\n<p>Một trong những ưu điểm tuyệt vời của chiếc <em>iphone X quốc tế</em> l&agrave; hiệu năng vượt trội của n&oacute;. Tương tự như iPhone 8 Plus, iPhone X cũng được t&iacute;ch hợp con chip A11 Bionic 6 nh&acirc;n gi&uacute;p tăng hiệu suất v&agrave; tốc độ khi xử l&yacute; t&aacute;c vụ đa nhiệm. Đ&acirc;y được xem l&agrave; con chip &ldquo;xịn x&ograve;&rdquo; nhất m&agrave; Nh&agrave; T&aacute;o đ&atilde; ph&aacute;t triển.</p>\r\n\r\n<p>Đ&uacute;ng với c&aacute;i t&ecirc;n của m&igrave;nh, chip Bionic c&oacute; khả năng xử l&yacute; nhiều t&aacute;c vụ phức tạp v&agrave; tối ưu hiệu năng của m&aacute;y. Con chip bao gồm 2 nh&acirc;n mạnh Monsoon v&agrave; 4 nh&acirc;n tiết kiệm điện Mistral. Trong đ&oacute;, nh&acirc;n Monsoon A11 mạnh hơn 25% so với nh&acirc;n Hurricane v&agrave; nh&acirc;n Mistral mạnh hơn 70% so với nh&acirc;n Zephyr. So với chip A10 chỉ c&oacute; khoảng 3,3 tỷ b&oacute;ng b&aacute;n dẫn, chup A11 sở hữu tối 4,3 tỷ b&oacute;ng.</p>\r\n\r\n<p>Đặc biệt, chip A11 c&ograve;n được t&iacute;ch hợp khu vực Neural Engine d&agrave;nh ri&ecirc;ng cho việc xử l&yacute; t&aacute;c vụ tr&iacute; tuệ nh&acirc;n tạo. Neural Engine c&oacute; khả năng xử l&yacute; hơn 600 tỷ ph&eacute;p to&aacute;n trong chỉ vỏn vẹn 1 gi&acirc;y. Nhờ vậy, chức năng Face ID của chiếc iPhone X hoạt động mượt m&agrave; v&agrave; nhanh ch&oacute;ng hơn người tiền nhiệm rất nhiều.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Hiệu năng iPhone X quốc tế vô đối\" class=\"aligncenter size-full wp-image-23984\" src=\"https://galaxydidong.vn/wp-content/uploads/2017/11/iphone-x-64gb-quoc-te-cu-3.png\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Con chip A11 tr&ecirc;n iPhone X c&oacute; khả năng xử l&yacute; t&aacute;c vụ tr&iacute; tuệ nh&acirc;n tạo</em></p>\r\n\r\n<h3><strong>3. Camera k&eacute;p chụp ảnh cực chuẩn</strong></h3>\r\n\r\n<p>Cũng giống như iPhone 8, chiếc iPhone X cũng sử dụng cụm camera k&eacute;p với độ ph&acirc;n giải l&ecirc;n đến 12MP, bao gồm:</p>\r\n\r\n<ul>\r\n	<li>Camera g&oacute;c rộng c&oacute; khẩu độ f/1.8</li>\r\n	<li>Camera chụp xa c&oacute; khẩu độ f/2.8 với chức năng zoom quang học</li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Iphone X 64Gb Quốc Tế Cũ\" class=\"aligncenter size-full wp-image-23985\" src=\"https://galaxydidong.vn/wp-content/uploads/2017/11/iphone-x-64gb-quoc-te-cu-4.png\" style=\"height:534px; width:800px\" title=\"Camera kép chụp ảnh cực chuẩn\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Bộ camera k&eacute;p hỗ trợ chụp ảnh tối ưu</em></p>\r\n\r\n<p>Cả hai đều được trang bị chức năng chống rung quang học. Nhờ vậy, chụp ảnh khi đang di chuyển hay v&ocirc; t&igrave;nh rung tay cũng kh&ocirc;ng khiến ảnh bị nh&ograve;e hay mờ. B&ecirc;n cạnh đ&oacute;, m&aacute;y cũng được trang bị camera trước c&oacute; độ ph&acirc;n giải 7 MP gi&uacute;p mang đến những tấm h&igrave;nh chụp selfie đẹp.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, kh&ocirc;ng thể kh&ocirc;ng nhắc đến chức năng x&oacute;a ph&ocirc;ng v&agrave; chỉnh s&aacute;ng theo điều kiện m&ocirc;i trường. Một số kh&aacute;ch h&agrave;ng từng trải nghiệm đều cho rằng chất lượng ảnh x&oacute;a ph&ocirc;ng của iPhone X kh&ocirc;ng thua g&igrave; chiếc m&aacute;y ảnh cơ được lắp ống k&iacute;nh chuy&ecirc;n dụng.</p>\r\n\r\n<h3><strong>4. Pin </strong><strong>iPhone X quốc tế đ&atilde; </strong><strong>được n&acirc;ng cấp</strong></h3>\r\n\r\n<p>Như một truyền thống, chiếc <em>iphone x quốc tế</em> được Apple trang bị pin với dung lượng khủng. so với chiếc iPhone 7, iPhone X sạc đầy pin sử dụng được nhiều hơn tới 2 giờ. B&ecirc;n cạnh đ&oacute;, điện thoại c&ograve;n c&oacute; tốc độ sạc pin nhanh v&agrave; được hỗ trợ chức năng sạc kh&ocirc;ng d&acirc;y tiện dụng.</p>\r\n\r\n<h3><strong>5. Cung cấp h&agrave;ng loạt t&iacute;nh năng mới mẻ</strong></h3>\r\n\r\n<p>Nhờ sự hỗ trợ của con chip A11, iPhone X c&oacute; thể thực hiện những chức năng th&ocirc;ng minh như nhận diện khu&ocirc;n mặt Face ID. Thoe th&ocirc;ng tin của nh&agrave; sản xuất, tỷ lệ nhận diện sai của iPhone X chỉ tầm 1/1.000.000 m&agrave; th&ocirc;i. So với Touch ID, Face ID kh&ocirc;ng chỉ tiện lợi m&agrave; c&ograve;n bảo mật tốt hơn 20 lần. Thậm ch&iacute;, Face ID c&ograve;n c&oacute; thể nhận diện được những thay đổi nhỏ của chủ nh&acirc;n như để r&acirc;u hay trang điểm.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Face ID là chức năng nổi bật trên iPhone X\" class=\"aligncenter size-full wp-image-23986\" src=\"https://galaxydidong.vn/wp-content/uploads/2017/11/iphone-x-64gb-quoc-te-cu-5.png\" style=\"height:500px; width:800px\" title=\"Face ID là chức năng nổi bật trên iPhone X\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Face ID l&agrave; chức năng nổi bật tr&ecirc;n iPhone X</em></p>\r\n', 0, '625c167c53781.jpg,625c167c53962.jpg,625c167c53ab6.jpg', 12000000),
(135, 88, 'Iphone 12', '<p>+ Giảm thêm tới 1% cho thành viên Smember (áp dụng tùy sản phẩm</p>\r\n<p>+ Giảm thêm 500.000đ khi thanh toán qua ví thẻ tín dụng VP Bank cho đơn hàng từ 10 triệu</p>\r\n<p>+ Giá thu cao - Thủ tục nhanh chóng - Trợ giá tốt nhất Thu cũ đổi mới: </p>', '<h2><strong>Đ&aacute;nh gi&aacute; iPhone 12 - Si&ecirc;u ph&acirc;̉m khẳng định đẳng c&acirc;́p</strong></h2>\r\n\r\n<p>Phi&ecirc;n bản iPhone 12 thường sở hữu nhiều điểm mới đặc biệt l&agrave; về thiết kế v&agrave; cấu h&igrave;nh so với c&aacute;c thế hệ trước đ&oacute;.</p>\r\n\r\n<h3><strong>Thiết kế&nbsp;</strong></h3>\r\n\r\n<p>iPhone 12 g&acirc;y ấn tượng với người d&ugrave;ng bởi thiết kế vu&ocirc;ng vức quen thuộc, đ&acirc;y l&agrave; thiết kế đ&atilde; từng xuất hiện tr&ecirc;n thế hệ iPhone 5 trước đ&oacute;. Điện thoại được ho&agrave;n thiện mỏng hơn với cụm camera lớn hơn.</p>\r\n\r\n<p>Đặc biệt, m&aacute;y được trang bị một khung viền th&eacute;p kh&ocirc;ng gỉ. Mặt lưng&nbsp;<a href=\"https://cellphones.com.vn/mobile/apple/iphone-12-vna.html\">điện thoại iPhone 12</a>&nbsp;được trang bị một mặt k&iacute;nh Ceramic Shield b&oacute;ng bẩy, g&acirc;y được sự ch&uacute; &yacute; của người đối diện.</p>\r\n\r\n<p>Về k&iacute;ch thước cụ thể, m&aacute;y c&oacute; th&ocirc;ng số từng cạnh lần lượt l&agrave; 146.7 x 71.5 x 7.4 mm c&ugrave;ng c&acirc;n nặng khoảng 164g. Tổng thể, m&aacute;y kh&aacute; mỏng nhẹ th&iacute;ch hợp với cả người d&ugrave;ng nam v&agrave; nữ cũng như th&iacute;ch hợp với nhiều k&iacute;ch thước tay kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"Đánh giá thiết kế iPhone 12\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/apple/iphone-12-anh-10.jpg\" /></p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh&nbsp;</strong></h3>\r\n\r\n<p>M&agrave;n h&igrave;nh l&agrave; một điểm nhấn tr&ecirc;n iPhone 12. M&aacute;y được trang bị tấm nền Super Retina XDR OLED, thay v&igrave; IPS LCD như thế hệ trước đ&oacute; nhờ vậy người d&ugrave;ng sẽ cảm nhận được khả năng hiển thị sinh động, m&agrave;u sắc đậm đ&agrave; cũng như độ tương phản cao.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, điện thoại được trang bị một m&agrave;n h&igrave;nh k&iacute;ch thước kh&aacute; lớn, bằng với&nbsp;<a href=\"https://cellphones.com.vn/iphone-12-pro.html\">iPhone 12 Pro</a>&nbsp;l&agrave;&nbsp;6.1 inch. C&ugrave;ng với đ&oacute; l&agrave; độ ph&acirc;n giải 1.170 x 2.532 pixels nhờ đ&oacute; mang lại khả năng hiển thị với h&igrave;nh ảnh đẹp mắt. M&agrave;n h&igrave;nh với tần số qu&eacute;t 60Hz mang lại trải nghiệm mượt m&agrave;, từ xem phim đến chơi game.</p>\r\n\r\n<p>Phần viền m&agrave;n h&igrave;nh tr&ecirc;n thế hệ iPhone 12 cũng nhỏ v&agrave; mỏng hơn rất nhiều so với c&aacute;c thế thệ trước. Nhờ đ&oacute; người d&ugrave;ng c&oacute; kh&ocirc;ng giản trải nghiệm rộng hơn, m&agrave;n h&igrave;nh hiển thị được nhiều chi tiết hơn.</p>\r\n\r\n<p>Ngo&agrave;i ra, m&agrave;n h&igrave;nh tr&ecirc;n thiết bị cũng được trang bị c&ocirc;ng nghệ True Tone gi&uacute;p tự động điều chỉnh độ s&aacute;ng m&agrave;n h&igrave;nh theo độ s&aacute;ng của m&ocirc;i trường gi&uacute;p con mắt của người d&ugrave;ng thoải m&aacute;i hơn khi sử dụng.</p>\r\n\r\n<p><img alt=\"Đánh giá màn hình iPhone 12\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/apple/iphone-12-anh-2.jpg\" /></p>\r\n\r\n<h3><strong>Cấu h&igrave;nh&nbsp;</strong></h3>\r\n\r\n<p>Ngo&agrave;i nhiều n&acirc;ng cấp về thiết kế, cấu h&igrave;nh cũng l&agrave; một điểm n&acirc;ng cấp lớn. Cấu h&igrave;nh n&agrave;y gi&uacute;p iPhone 12 trở th&agrave;nh l&agrave; một trong những chiếc điện thoại mạnh mẽ nhất thời điểm ra mắt.</p>\r\n\r\n<p>Cụ thể, m&aacute;y được trang bị con chip Apple A14 Bionic c&ugrave;ng dung lượng RAM 4GB c&ugrave;ng bộ nhớ trong 64GB (Bản ti&ecirc;u chuẩn). Con chip A14 mới n&agrave;y được sản xuất tr&ecirc;n tiến tr&igrave;nh 5nm với hơn 11 tỷ b&oacute;ng b&aacute;n dẫn mang lại hiệu năng CPU tăng 40% v&agrave; 30% GPU so với thế hệ trước.</p>\r\n\r\n<p>Cấu h&igrave;nh vượt trội n&agrave;y gi&uacute;p thiết bị chiến tốt c&aacute;c tựa game hot tr&ecirc;n thị trường như Li&ecirc;n Minh Huyền Thoại, Li&ecirc;n Qu&acirc;n Mobile, PUBG Mobile, Call Of Duty Mobile, Genshin Impact Mobile,&hellip;</p>\r\n\r\n<p>Ở c&aacute;c phần mềm chấm điểm hiệu năng, iPhone 12 cho điểm số kh&aacute; ấn tượng. Cụ thể, điện thoại cho 592.383 điểm AnTuTu trong đ&oacute; điểm CPU l&ecirc;n tới 182.690 điểm v&agrave; GPU l&agrave; 248.724 điểm.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, '625cc91656657.png,625cc91656a3f.png,625cc91656e28.png', 1690000),
(137, 88, 'iPhone 8 Plus', '<p>+ Sản phẩm cam kết nguyên zin Apple, shop rất nhiều hàng đủ màu và dung lượng, giá luôn rẻ so với thị trường, có trả góp 0%.\r\n</p>\r\n<p>+ Giảm giá Combo sạc 20w Apple chính hãng + cáp nhanh chỉ còn 899.000đ.</p>\r\n<p>+ Phiến trị giá 500.000đ</p>', '<h2>Thừa hưởng những thiết kế đ&atilde; đạt đến độ chuẩn mực, thế hệ&nbsp;iPhone 8 Plus&nbsp;thay đổi phong c&aacute;ch b&oacute;ng bẩy hơn v&agrave; bổ sung h&agrave;ng loạt t&iacute;nh năng cao cấp cho trải nghiệm sử dụng v&ocirc; c&ugrave;ng tuyệt vời.</h2>\r\n\r\n<h3>Thiết kế từ&nbsp;k&iacute;nh v&agrave; kim loại</h3>\r\n\r\n<p>Smartphone&nbsp;iPhone 8 Plus&nbsp;giữ nguy&ecirc;n ho&agrave;n to&agrave;n những đường n&eacute;t thiết kế đ&atilde; ho&agrave;n thiện từ thế hệ trước nhưng sử dụng phong c&aacute;ch 2 mặt k&iacute;nh cường lực kết hợp bộ khung kim loại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h7-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h7-1.jpg\" title=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" /></a></p>\r\n\r\n<p>Mặt lưng k&iacute;nh bo cong 2.5D thời thượng, khung kim loại được gia cố cứng c&aacute;p, bền bỉ hơn với 7 lớp xử l&yacute; m&agrave;u sơn, hạn chế bong tr&oacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h9.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h9.jpg\" title=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" /></a></p>\r\n\r\n<p>Thay đổi n&agrave;y đồng thời loại bỏ chi tiết đường anten thừa ở mặt lưng như c&aacute;c thế hệ trước, mang đến vẻ đẹp ho&agrave;n hảo hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/114114/iphone-8-plus-256gb-h0.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/114114/iphone-8-plus-256gb-h0.jpg\" title=\"Thiết kế điện thoại iPhone 8 Plus 64GB\" /></a></p>\r\n', 0, '625cce0b49d7b.jpg,625cce0b4a163.jpg,625cce0b4a54b.jpg', 6790000),
(138, 88, 'Iphone 7 plus', '<p>+ Sản phẩm cam kết nguyên zin Apple, shop rất nhiều hàng đủ màu và dung lượng, giá luôn rẻ so với thị trường, có trả góp 0%.\r\n</p>\r\n<p>+ Giảm giá Combo sạc 20w Apple chính hãng + cáp nhanh chỉ còn 899.000đ.</p>\r\n<p>+ Phiến trị giá 500.000đ</p>', '<h2>Phi&ecirc;n bản&nbsp;<strong>iPhone 7 Plus 128GB cũ</strong>&nbsp;like new sở hữu dung lượng lưu trữ kh&aacute; rộng r&atilde;i. Đồng thời, trải nghiệm&nbsp;<a href=\"https://www.duchuymobile.com/iphone-7-plus-128gb-cu\" target=\"_blank\">iPhone 7 Plus 128GB quốc tế cũ</a>&nbsp;gi&aacute; rẻ vẫn c&ograve;n chất như m&aacute;y mới, nhất l&agrave; khi chụp ảnh với cụm camera k&eacute;p.</h2>\r\n\r\n<p><img alt=\"mau-sac-iphone-7-plus-128gb-quoc-te-cu-gia-re-duchuymobile\" src=\"https://www.duchuymobile.com/image/data/IPHONE/iphone-7-plus-128gb-quoc-te-cu/mau-sac-iphone-7-plus-128gb-quoc-te-cu-gia-re-duchuymobile.jpg\" /></p>\r\n\r\n<p><em>iPhone 7 Plus 128GB quốc tế cũ like new gi&aacute; rẻ với nhiều t&ugrave;y chọn m&agrave;u sắc tại Đức Huy Mobile.</em></p>\r\n\r\n<p>iPhone 7 Plus 128GB cũ vẫn mang dấp d&aacute;ng &ldquo;người xưa&rdquo;. Tuy nhi&ecirc;n, iPhone 7 Plus quốc tế cũ gi&aacute; rẻ c&oacute; phần bền bỉ hơn với khả năng chống nước IP67.</p>\r\n\r\n<p><img alt=\"mua-iphone-7-plus-128gb-quoc-te-cu-gia-re-duchuymobile\" src=\"https://www.duchuymobile.com/image/data/IPHONE/iphone-7-plus-128gb-quoc-te-cu/mua-iphone-7-plus-128gb-quoc-te-cu-gia-re-duchuymobile.jpg\" /></p>\r\n\r\n<p>Ngo&agrave;i ra, so s&aacute;nh&nbsp;<a href=\"https://www.duchuymobile.com/iphone-7-plus-cu\">iPhone 7 Plus cũ gi&aacute; rẻ</a><strong>&nbsp;</strong>v&agrave; những thế hệ trước đều c&oacute; độ ho&agrave;n thiện cao v&agrave; cho cảm gi&aacute;c cầm tay thoải m&aacute;i. Đồng thời, dải ăng-ten ở mặt lưng cũng được thiết kế lại v&agrave; tăng t&iacute;nh thẩm mỹ cho sản phẩm.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh Ch&acirc;n thật đến từng chi tiết</h3>\r\n\r\n<p><strong>iPhone 7 Plus 128GB cũ</strong>&nbsp;gi&aacute; rẻ mang đến kh&ocirc;ng gian hiển thị sống động hơn. Đ&oacute; l&agrave; nhờ m&agrave;n h&igrave;nh iPhone 7 Plus quốc tế cũ được hỗ trợ DCI-P3, cho gam m&agrave;u rộng v&agrave; n&acirc;ng cao khả năng t&aacute;i tạo m&agrave;u.</p>\r\n\r\n<p><img alt=\"man-hinh-iphone-7-plus-128gb-quoc-te-cu-gia-re-duchuymobile\" src=\"https://www.duchuymobile.com/image/data/IPHONE/iphone-7-plus-128gb-quoc-te-cu/man-hinh-iphone-7-plus-128gb-quoc-te-cu-gia-re-duchuymobile.jpg\" /></p>\r\n\r\n<p><em>M&agrave;n h&igrave;nh iPhone 7 PLus 128GB quốc tế 99% được người d&ugrave;ng đ&aacute;nh gi&aacute; cao.</em></p>\r\n\r\n<p>V&igrave; thế, người d&ugrave;ng rất h&agrave;i l&ograve;ng với chất lượng hiển thị của m&aacute;y d&ugrave; trang bị phần cứng vẫn giống đ&agrave;n anh. Hơn nữa, iPhone 7 Plus 128GB quốc tế cũ like new 99% gi&aacute; rẻ c&ograve;n l&agrave; thiết bị giải tr&iacute;, xem phim l&yacute; tưởng với m&agrave;n h&igrave;nh 5.5 inches FullHD.</p>\r\n', 0, '625ccfdd2fe0b.jpg,625ccfdd301f3.jpg,625ccfdd305db.jpg', 5690000);
INSERT INTO `tbl_product` (`id`, `category_id`, `name`, `content`, `descripition`, `product_hot`, `img`, `price`) VALUES
(139, 2, 'Samsung Galaxy A53', '<p>+ Giảm thêm tới 1% cho thành viên Smember (áp dụng tùy sản phẩm)\r\n</p>\r\n<p>+ Giảm thêm 500.000đ khi thanh toán qua ví thẻ tín dụng VP Bank cho đơn hàng từ 10 triệu</p>\r\n<p>+ Thu cũ đổi mới: Giá thu cao - Thủ tục nhanh chóng - Trợ giá tốt nhất</p>', '<h2><strong>Điện thoại Samsung Galaxy A53 &ndash; Hiệu năng si&ecirc;u đỉnh, vi&ecirc;n pin si&ecirc;u l&acirc;u</strong></h2>\r\n\r\n<p>Tiếp nối sự th&agrave;nh c&ocirc;ng của d&ograve;ng điện thoại tầm trung &ndash; gi&aacute; rẻ, Samsung lại cho ra mắt tiếp thế hệ tiếp theo mang t&ecirc;n&nbsp;<strong>Galaxy A53</strong>. Sản phẩm mang hiệu suất tốt, sẵn s&agrave;ng mang đến cho bạn những gi&acirc;y ph&uacute;t trải nghiệm chơi game, xem phim mượt m&agrave;.</p>\r\n\r\n<h3><strong>Thiết kế gradient bắt mắt, m&agrave;n h&igrave;nh 6.5 inches&nbsp;</strong></h3>\r\n\r\n<p>Mặt lưng của điện thoại Samsung A53 chỉ được ho&agrave;n thiện từ nhựa song lại mang đến cho điện thoại trọng lượng tối ưu gi&uacute;p hỗ trợ cầm nắm thoải m&aacute;i. Song vẻ ngo&agrave;i kh&ocirc;ng bị mất gi&aacute; trị bởi thiết kế gradient chuyển m&agrave;u nhẹ, kết hợp với những đường n&eacute;t cắt b&oacute;ng theo hiệu ứng &aacute;nh s&aacute;ng sẵn s&agrave;ng bắt mắt người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"Điện thoại Samsung Galaxy A53 \" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-a53-1.png\" /></p>\r\n\r\n<p>M&agrave;n h&igrave;nh Super AMOLED Infinity-O 6.5 inch độ ph&acirc;n giải Full-HD+ cho h&igrave;nh ảnh ch&acirc;n thực, r&otilde; n&eacute;t, sống động, gi&uacute;p người d&ugrave;ng dễ d&agrave;ng quan s&aacute;t nhiều nội dung hơn trước. . Th&ecirc;m v&agrave;o đ&oacute;, tần số qu&eacute;t 120Hz hỗ trợ trải nghiệm chuyển động mượt m&agrave;, trơn tru trong qu&aacute; tr&igrave;nh chơi game, lướt web, lướt Facebook,&hellip;</p>\r\n\r\n<p>Mặt trước điện thoại l&agrave; tấm nền được trang bị lớp k&iacute;nh cường lực Gorilla Glass 5 hạn chế t&aacute;c động từ ngoại lực l&agrave;m nứt vỡ, gi&uacute;p bạn an t&acirc;m hơn trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n\r\n<h3><strong>Trọng lượng nhẹ, chuẩn chống bụi nước</strong></h3>\r\n\r\n<p>Samsung Galaxy A53 c&oacute; trọng lượng nhẹ n&ecirc;n dễ d&agrave;ng để trong t&uacute;i x&aacute;ch, ba l&ocirc;, bỏ t&uacute;i quần mang đi m&agrave; kh&ocirc;ng cảm thấy nặng tay hay vướng v&iacute;u.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, thiết bị c&ograve;n được trang bị c&ocirc;ng nghệ chống bụi nước IP67. Đ&acirc;y l&agrave; cấp bảo vệ chống bụi ho&agrave;n to&agrave;n v&agrave; chống nước khi ng&acirc;m thiết bị ở độ s&acirc;u 1m trong v&ograve;ng nửa tiếng đồng hồ. Như vậy trong những ng&agrave;y trời ẩm ướt, mưa, bạn cũng kh&ocirc;ng c&ograve;n qu&aacute; lo lắng việc thiết bị sẽ bị hư hỏng.</p>\r\n', 0, '625cd2fd10911.jpg,625cd2fd10cf9.jpg,625cd2fd110e1.jpg', 8900000),
(140, 2, 'Samsung Galaxy A12', '<p>+ Giảm thêm tới 1% cho thành viên Smember (áp dụng tùy sản phẩm)\r\n</p>\r\n<p>+ Giảm thêm 500.000đ khi thanh toán qua ví thẻ tín dụng VP Bank cho đơn hàng từ 10 triệu</p>\r\n<p>+ Thu cũ đổi mới: Giá thu cao - Thủ tục nhanh chóng - Trợ giá tốt nhất</p>', '<h2><strong>Điện thoại Samsung Galaxy A12 &ndash; Smartphone gi&aacute; rẻ cấu h&igrave;nh tốt v&agrave; pin tr&acirc;u</strong></h2>\r\n\r\n<p><strong>Samsung A12</strong>&nbsp;l&agrave; một chiếc smartphone gi&aacute; rẻ nhưng sở hữu cấu h&igrave;nh ổn định c&ugrave;ng với vi&ecirc;n pin 5000mAh cho thời lượng suốt ng&agrave;y d&agrave;i. B&ecirc;n cạnh đ&oacute; điện thoại cũng sở hữu thiết kế thời trang v&agrave; ph&ugrave; hợp với xu hướng.</p>\r\n\r\n<h3><strong>Thiết kế đơn giản, 4 phi&ecirc;n bản m&agrave;u, v&acirc;n tay cạnh b&ecirc;n</strong></h3>\r\n\r\n<p>Galaxy A12 c&oacute; thiết kế đơn giản với bốn g&oacute;c cạnh được bo tr&ograve;n mềm mại c&ugrave;ng với hai cạnh b&ecirc;n được v&aacute;t cong nhẹ nh&agrave;ng tạo cảm gi&aacute;c cầm thoải m&aacute;i cho người d&ugrave;ng.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; smartphone c&ograve;n được cho ra mắt với nhiều phi&ecirc;n bản m&agrave;u Đen, Trắng, Xanh thời trang để bạn c&oacute; thể lựa chọn m&agrave;u sắc y&ecirc;u th&iacute;ch.</p>\r\n\r\n<p><img alt=\"Thiết kế đơn giản, 4 phiên bản màu và cảm biến vân tay tích hợp ở mặt lưng\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-a12-1_1.jpg\" /></p>\r\n\r\n<p>Samsung A12 mặc d&ugrave; c&oacute; thiết kế nguy&ecirc;n khối v&agrave; chỉ sử dụng chất liệu nhựa nhưng lại được ho&agrave;n thiện v&ocirc; c&ugrave;ng tỉ mỉ v&agrave; ch&iacute;nh x&aacute;c n&ecirc;n vẫn mang đến cảm gi&aacute;c cao cấp khi chạm.&nbsp;M&aacute;y được hỗ trợ v&acirc;n tay cạnh b&ecirc;n gi&uacute;p người d&ugrave;ng mở kh&oacute;a điện thoại nhanh ch&oacute;ng.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh giọt nước&nbsp; 6.5 inch v&agrave; tấm nền TFT LCD t&aacute;i tạo m&agrave;u tốt</strong></h3>\r\n\r\n<p>Mặc d&ugrave; l&agrave; smartphone gi&aacute; rẻ nhưng Galaxy A12 vẫn sở hữu thiết kế m&agrave;n h&igrave;nh bắt kịp với xu hướng với m&agrave;n h&igrave;nh giọt nước v&agrave; được bo tr&ograve;n theo kiểu d&aacute;ng của smartphone. M&agrave;n h&igrave;nh 6.5 inch tr&agrave;n viền cho người d&ugrave;ng g&oacute;c nh&igrave;n rộng, thoải m&aacute;i tận hưởng kh&ocirc;ng gian hiển thị.</p>\r\n\r\n<p><img alt=\"Màn hình đục lỗ 6.4 inch và tấm nền TFT LCD tái tạo màu tốt\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-a12-2_1.jpg\" /></p>\r\n\r\n<p>Sử dụng tấm nền TFT LCD thay v&igrave; c&aacute;c loại m&agrave;n h&igrave;nh LCD th&ocirc;ng thường mang đến khả năng t&aacute;i tạo m&agrave;u tốt hơn, cho h&igrave;nh ảnh hiển thị c&oacute; m&agrave;u sắc ch&acirc;n thực v&agrave; sống động.</p>\r\n\r\n<p>Smartphone c&ograve;n c&oacute; độ ph&acirc;n giải HD+ mặc d&ugrave; chưa thực sự qu&aacute; xuất sắc nhưng tr&ecirc;n một thiết bị gi&aacute; rẻ th&igrave; m&agrave;n h&igrave;nh A12 đ&atilde; đạt chất lượng rất tốt.</p>\r\n\r\n<h3><strong>Hệ thống 4 camera sau với ống k&iacute;nh g&oacute;c si&ecirc;u rộng v&agrave; camera trước 8MP</strong></h3>\r\n\r\n<p>Samsung Galaxy A12 cũng mang đến cho người d&ugrave;ng khả năng chụp ảnh ấn tượng khi trang bị 4 camera sau với camera ch&iacute;nh 48MP g&oacute;c rộng, camera g&oacute;c si&ecirc;u rộng 5MP v&agrave; camera x&oacute;a ph&ocirc;ng 2MP c&ugrave;ng camera cận cảnh 2MP.</p>\r\n\r\n<p>M&aacute;y hỗ trợ đầy đủ c&aacute;c t&iacute;nh năng chụp ảnh th&ocirc;ng minh như lấy n&eacute;t tự động, chống rung,&hellip;đồng thời hỗ trợ quay phim với chất lượng 1080p sắc n&eacute;t để bạn c&oacute; thể quay lại mọi khoảnh khắc đ&aacute;ng nhớ, chụp lại c&aacute;c t&agrave;i liệu quan trọng một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n', 0, '625cd3df9cb18.jpg,625cd3df9cf00.jpg,625cd3df9d2e8.jpg', 3800000),
(141, 2, 'Samsung Galaxy A33 ', '<p>+ Màn hình giải trí lớn, sắc nét từng chi tiết - 6.5 inch, Full HD+ cho hình ảnh sắc nét, chân thực\r\n</p>\r\n<p>+ 4 camera hỗ trợ chụp ảnh chuyên nghiệp - Camera chính lến đến 64 MP ghi lại trọn vẹn chi tiết đáng giá</p>\r\n<p>+ Cấu hình ổn định', '<h2><strong>Đi&ecirc;̣n thoại Samsung Galaxy A33 - Smartphone c&acirc;́u hình t&ocirc;́t trong t&acirc;̀m tay bạn</strong></h2>\r\n\r\n<p><em>Mang thi&ecirc;́t k&ecirc;́ thời thượng, màn hình giải trí lớn cùng hi&ecirc;̣u năng v&acirc;̣n hành 5G hi&ecirc;̣u quả,&nbsp;</em><em><strong>Samsung A33</strong>&nbsp;chính là chi&ecirc;́c smartphone c&acirc;́u hình t&ocirc;́t với mức giá hợp lý dành cho người dùng c&ocirc;ng ngh&ecirc;̣ ph&ocirc;̉ th&ocirc;ng.</em></p>\r\n\r\n<h3><strong>Thi&ecirc;́t k&ecirc;́ t&ocirc;́i giản với nhi&ecirc;̀u lựa chọn màu sắc h&acirc;́p d&acirc;̃n</strong></h3>\r\n\r\n<p>Đi&ecirc;̣n thoại Galaxy A33 5G là chi&ecirc;́c smartphone ph&acirc;n khúc trung c&acirc;́p với ki&ecirc;̉u dáng đ&acirc;̀y cu&ocirc;́n hút như m&ocirc;̣t chi&ecirc;́c smartphone cao c&acirc;́p. Mang phong cách thi&ecirc;́t k&ecirc;́ t&ocirc;́i giản, Samsung A33 có vẻ ngoài kh&ocirc;ng c&acirc;̀u kỳ, mặt lưng tinh gọn và cụm camera chỉ hi&ecirc;̉n thị từng &ocirc;́ng kính thay vì đi theo cụm chữ nh&acirc;̣t, làm n&ecirc;n nét thanh lịch và khác bi&ecirc;̣t so với những chi&ecirc;́c smartphone sặc sỡ hi&ecirc;̣n nay.</p>\r\n\r\n<p><img alt=\"Thiết kế tối giản với nhiều lựa chọn màu sắc hấp dẫn\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/dien-thoai-samsung-galaxy-a33-1.jpg\" /></p>\r\n\r\n<p>Đi&ecirc;̣n thoại Samsung Galaxy A33 có lớp vỏ ngoài được ch&ecirc;́ tác từ nhựa b&ecirc;̀n cứng cáp, cùng với lớp kính phủ mặt trước tăng th&ecirc;m nét sang trọng cho chi&ecirc;́c máy. Đi kèm với nhi&ecirc;̀u lựa chọn màu sắc như Đen, Xanh Bi&ecirc;̉n hoặc Trắng, điện thoại&nbsp; A33 sẽ là chi&ecirc;́c smartphone thời thượng, phù hợp cho giới trẻ.</p>\r\n\r\n<h3><strong>Màn hình giải trí kích cỡ lớn với cảm bi&ecirc;́n v&acirc;n tay quang học</strong></h3>\r\n\r\n<p>Galaxy A33 thích hợp cho cả nhu c&acirc;̀u đọc báo, làm vi&ecirc;̣c l&acirc;̃n giải trí bắt mắt. Chi&ecirc;́c máy sở hữu màn hình r&ocirc;̣ng 6.5 inch, đ&ocirc;̣ ph&acirc;n giải Full HD+ (2340 x 1080 pixels) tr&ecirc;n n&ecirc;̀n màu Super AMOLED đặc trưng của Samsung. Thi&ecirc;́t k&ecirc;́ Infinity-O với vi&ecirc;̀n bezel mỏng sẽ giúp hình ảnh hi&ecirc;̉n thị tr&ecirc;n điện thoại trở n&ecirc;n ch&acirc;n thực và thoáng đãng.</p>\r\n\r\n<p><img alt=\"Màn hình giải trí kích cỡ lớn với cảm biến vân tay quang học\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/dien-thoai-samsung-galaxy-a33-2.jpg\" /></p>\r\n\r\n<p>Đặc bi&ecirc;̣t, Samsung Galaxy A33 5G được trang bị cả c&ocirc;ng ngh&ecirc;̣ cảm bi&ecirc;́n v&acirc;n tay quang học, với b&ocirc;̣ ph&acirc;̣n cảm bi&ecirc;́n được tích hợp &acirc;̉n dưới màn hình 6.5 inch của máy. Giờ đ&acirc;y, chỉ với m&ocirc;̣t l&acirc;̀n chạm nhẹ l&ecirc;n màn hình, bạn đã có th&ecirc;̉ mở khóa Samsung A33 ti&ecirc;̣n lợi và nhanh chóng hơn so với các phương thức m&acirc;̣t kh&acirc;̉u truy&ecirc;̀n th&ocirc;́ng.</p>\r\n', 0, '625cd5d1be044.jpg,625cd5d1be42c.jpg,625cd5d1be814.jpg', 8400000),
(142, 2, 'Samsung Galaxy A23', '<p>+ Nâng cao trải nghiệm với màn hình chất lượng - Màn hình LCD 6.6 inch Full HD+\r\n</p>\r\n<p>+ Hiệu năng ấn tượng mạnh mẽ - Snapdragon 680 (SM6225), RAM dung lượng 4GB</p>\r\n<p>+ Cấu hình ổn định, xử lí đa dạng tác vụ - Vi xử lý Dimensity 800U hỗ trợ 5', '<h2><strong>Điện thoại Samsung Galaxy A23 mạnh mẽ, mượt m&agrave; v&agrave; camera&nbsp;</strong><strong>chất lượng</strong></h2>\r\n\r\n<p><strong>Samsung Galaxy A23</strong>&nbsp;l&agrave; thiết kế g&acirc;y ấn tượng mạnh mẽ với người d&ugrave;ng bởi cấu h&igrave;nh ấn tượng cho hiệu năng mạnh mẽ. B&ecirc;n cạnh đ&oacute; l&agrave; tần số qu&eacute;t lớn v&agrave; đặc biệt l&agrave; gi&aacute; cả v&ocirc; c&ugrave;ng phải chăng. Chắc chắn với những g&igrave; được trang bị đ&acirc;y sẽ l&agrave; chiếc smartphone mang lại cho người d&ugrave;ng những trải nghiệm ấn tượng kh&oacute; qu&ecirc;n.</p>\r\n\r\n<h3><strong>Thiết kế thời trang, trẻ trung</strong></h3>\r\n\r\n<p>Điện thoại Samsung Galaxy A23 mang tr&ecirc;n m&igrave;nh thiết kế v&ocirc; c&ugrave;ng ấn tượng v&agrave; độc đ&aacute;o. Phần khung viền v&agrave; mặt lưng được ho&agrave;n thiện từ nhựa cao cấp mang đến sự nhẹ nh&agrave;ng v&agrave; bền bỉ đồng thời gi&uacute;p tiết kiệm gi&aacute; th&agrave;nh cho sản phẩm.</p>\r\n\r\n<p><img alt=\"Samsung Galaxy A23\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/SAMSSUNG-A23-4G-1.jpg\" /></p>\r\n\r\n<p><strong>Samsung Galaxy A23</strong>&nbsp;được thiết kế với nhiều phi&ecirc;n bản m&agrave;u sắc trẻ trung v&agrave; nổi bật ph&ugrave; hợp với đại đa số người d&ugrave;ng hiện nay. Bạn c&oacute; thể lựa chọn m&agrave;u đen, trắng, xanh hoặc t&iacute;m để ph&ugrave; hợp với sở th&iacute;ch của m&igrave;nh.</p>\r\n\r\n<h3><strong>N&acirc;ng cao trải nghiệm với m&agrave;n h&igrave;nh chất lượng</strong></h3>\r\n\r\n<p>Samsung Galaxy A23 được trang bị m&agrave;n TFT LCD k&iacute;ch thước lớn tới 6.6 inch. B&ecirc;n cạnh đ&oacute; l&agrave; độ ph&acirc;n giải Full HD+ v&ocirc; c&ugrave;ng sắc n&eacute;t cho h&igrave;nh ảnh trở n&ecirc;n chất lượng v&agrave; si&ecirc;u ấn tượng.</p>\r\n\r\n<p><img alt=\"Samsung Galaxy A23\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/SAMSSUNG-A23-4G-2.jpg\" /></p>\r\n\r\n<p>Ngo&agrave;i ra, Galaxy A23 cũng trang bị cho m&agrave;n h&igrave;nh tốc độ l&agrave;m tươi 60 Hz. Với trang bị n&agrave;y, người d&ugrave;ng c&oacute; thể thoải m&aacute;i trải nghiệm h&igrave;nh ảnh hiển thị si&ecirc;u mượt v&agrave; thao t&aacute;c chạm vuốt v&ocirc; c&ugrave;ng mượt m&agrave; để đắm ch&igrave;m trong c&aacute;c tựa game hấp dẫn.</p>\r\n', 0, '625cd703f35d7.jpg,625cd703f39bf.jpg,625cd703f3da7.jpg', 5190000),
(143, 6, 'Nokia G10', '<p>+ Màn hình giải trí siêu lớn - Màn hinh rộng 6.82\" HD+, viền mỏng\r\n</p>\r\n<p>+ Chụp ảnh đa chức năng - Cụm camera tròn 3 ống kính 13 MP</p>\r\n<p>+ Hiệu năng gaming chuyên biệt - Chip Mediatek G25 cùng 4GB RAM</p>', '<h2><strong>Đi&ecirc;̣n thoại Nokia G10 - Giá ph&ocirc;̉ th&ocirc;ng, phù hợp mọi người dùng</strong></h2>\r\n\r\n<p>Hãng đi&ecirc;̣n thoại lừng danh Nokia vừa qua đã giới thi&ecirc;̣u đ&ecirc;́n giới c&ocirc;ng ngh&ecirc;̣ sản ph&acirc;̉m mới mang t&ecirc;n&nbsp;<strong>Nokia G10</strong>. Đ&acirc;y là chi&ecirc;́c smartphone t&acirc;̣p trung vào hi&ecirc;̣u năng gaming với vi xử lý MediaTek G22 cùng dung lượng pin 5050mAh chỉ với mức giá rẻ giúp cho nhi&ecirc;̀u người d&ecirc;̃ dàng ti&ecirc;́p c&acirc;̣n.</p>\r\n\r\n<h3><strong>Thi&ecirc;́t k&ecirc;́ h&acirc;̀m h&ocirc;́ đẹp mắt, màn hình 6.5 inch vi&ecirc;̀n mỏng giải trí thỏa thích</strong></h3>\r\n\r\n<p>Nokia G10 áp dụng ng&ocirc;n ngữ thi&ecirc;́t k&ecirc;́ tr&ecirc;n các dòng smartphone Nokia, mang đ&ecirc;́n vẻ ngoài trang nh&atilde; &amp; đẹp mắt. Mặt lưng được bo cong giúp người dùng c&acirc;̀m nắm d&ecirc;̃ dàng tr&ecirc;n tay, với cụm camera sau thi&ecirc;́t k&ecirc;́ dạng tròn độc đ&aacute;o.</p>\r\n\r\n<p><img alt=\"Thiết kế hầm hố đẹp mắt, màn hình 6.38 inch viền mỏng giải trí thỏa thích\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/nokia/nokia-g10-3.jpg\" /></p>\r\n\r\n<p>Nokia G10 được trang bị màn hình kích thước 6.5 inch, đ&ocirc;̣ ph&acirc;n giải HD+ và dùng t&acirc;́m n&ecirc;̀n cao cấp c&oacute; độ s&aacute;ng cao. Thi&ecirc;́t k&ecirc;́ vi&ecirc;̀n si&ecirc;u mỏng ở hai b&ecirc;n chính là y&ecirc;́u t&ocirc;́ giúp cho Nookia G10 phù hợp với nhu c&acirc;̀u xem phim, chơi game và giải trí đa phương ti&ecirc;̣n.</p>\r\n', 0, '625cda8085069.jpg,625cda8085451.jpg,625cda8085839.jpg', 2990000),
(144, 6, 'Nokia G50', '<p>+ Màn hình giải trí siêu lớn - Màn hinh rộng 6.82\" HD+, viền mỏng\r\n</p>\r\n<p>+ Chụp ảnh đa chức năng - Cụm camera tròn 3 ống kính 13 MP</p>\r\n<p>+ Cấu hình ổn định, trải nghiệm giải trí mượt mà - Chip Snapdragon 480, kết nối 5G</p>', '<h2><strong>Đ&aacute;nh gi&aacute; Nokia G50 - Thỏa sức lướt mạng 5G si&ecirc;u tốc</strong></h2>\r\n\r\n<p>Hãng đi&ecirc;̣n thoại lừng danh Nokia v&acirc;̃n chưa ngừng cu&ocirc;̣c chơi trong thị ph&acirc;̀n smartphone. Bằng chứng l&agrave; việc hãng vừa tung ra thị trường sản ph&acirc;̉m mới mang t&ecirc;n&nbsp;<strong>Nokia G50</strong>&nbsp;- h&ocirc;̃ trợ mạng 5G với mức giá ph&ocirc;̉ th&ocirc;ng cho t&acirc;́t cả người y&ecirc;u c&ocirc;ng ngh&ecirc;̣.</p>\r\n\r\n<h3><strong>Phong cách đ&ocirc;̣c đáo, màn hình hi&ecirc;̉n thị r&ocirc;̣ng</strong></h3>\r\n\r\n<p>Đi&ecirc;̣n thoại Nokia G50 5G ti&ecirc;́p tục đi theo ng&ocirc;n ngữ thi&ecirc;́t k&ecirc;́ đã làm n&ecirc;n thành c&ocirc;ng của dòng smartphone Nokia G - phong cách đ&ocirc;̣c đáo. Từng đường nét tr&ecirc;n chiếc smartphone n&agrave;y mang hình dáng t&ocirc;́i giản, tinh t&ecirc;́ và thời thượng, với lựa chọn màu&nbsp;Ocean Blue và màu&nbsp;Midnight Sun&nbsp;bắt mắt. Đ&ocirc;̀ng thời, điện thoại Nokia cũng có th&acirc;n máy b&ecirc;̀n bỉ, mỏng gọn và nhẹ nhàng tr&ecirc;n tay giúp người dùng sử dụng thoải mái.</p>\r\n\r\n<p><img alt=\"Nokia G50\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/nokia/NOKIAG50-1.jpg\" /></p>\r\n\r\n<p>Nokia G50 sẽ mang đ&ecirc;́n trải nghi&ecirc;̣m giải trí bắt mắt qua màn hình 6.82 inch, có đ&ocirc;̣ ph&acirc;n giải HD+ (1560 x 720 pixels). Đặc bi&ecirc;̣t, Nokia lu&ocirc;n t&ocirc;́i ưu khả năng hi&ecirc;̉n thị màn hình ở ngoài trời. Vì th&ecirc;́ mà đi&ecirc;̣n thoại Nokia n&agrave;y được tăng cường đ&ocirc;̣ sáng đ&ecirc;̉ người dùng d&ecirc;̃ dàng theo dõi chi ti&ecirc;́t n&ocirc;̣i dung. Ph&acirc;̀n vi&ecirc;̀n màn hình được thu hẹp t&ocirc;́i đa giúp tăng tỷ l&ecirc;̣ hi&ecirc;̉n thị hình ảnh.</p>\r\n', 1, '625cdbe837c5e.jpeg,625cdbe838046.jpg,625cdbe83842e.jpg', 4900000),
(145, 6, 'Nokia C30 2GB', '<p>+ Không gian hiển thị tối đa - Màn hình IPS LCD cực lớn 6.82\", HD+\r\n</p>\r\n<p>+ Chụp ảnh đa chức năng - Cụm camera tròn 3 ống kính 13 MP</p>\r\n<p>+ Hiệu năng gaming chuyên biệt - Chip Mediatek G25 cùng 4GB RAM</p>', '<h3 dir=\"ltr\">Nokia C30 l&agrave; d&ograve;ng smartphone gi&aacute; rẻ của Nokia, rất được ưa chuộng tại thị trường hiện nay. Đ&acirc;y l&agrave; d&ograve;ng điện thoại gi&aacute; rẻ sở hữu m&agrave;n h&igrave;nh rộng, hiệu năng tốt, chụp ảnh chất lượng hay pin tr&acirc;u v&agrave; cực kỳ ph&ugrave; hợp với ng&acirc;n s&aacute;ch của nhiều người d&ugrave;ng.</h3>\r\n\r\n<p dir=\"ltr\"><img src=\"https://lh6.googleusercontent.com/JRXfYiLlUCpikyH6qI5nnyFEODDLyHL9c-0FRNQfVjg2ObmyRZI46WsYg3MD-chMFr5Yo7qctWVcDoQUhGqCP5UGuKoc1oBmXOXOkRB_u27iUW4-e6C6h4CKwT22nVuOLaI8OzFP=s0\" style=\"height:336px; width:602px\" /></p>\r\n\r\n<h3 dir=\"ltr\">Thiết kế cứng c&aacute;p từ nhựa polycarbonate với m&agrave;n h&igrave;nh LCD HD+</h3>\r\n\r\n<p dir=\"ltr\">Nokia C30 sở hữu thiết kế kh&ocirc;ng qu&aacute; kh&aacute;c biệt so với thế hệ Nokia C20. Với c&aacute;c g&oacute;c được bo tr&ograve;n ho&agrave;n hảo c&ugrave;ng logo Nokia nổi bật ph&iacute;a viền dưới m&agrave;n h&igrave;nh, Nokia C30 thực sự đ&atilde; chinh phục được người d&ugrave;ng với thiết kế tao nh&atilde; v&agrave; tinh tế. Cảm biến v&acirc;n tay được đặt ở ph&iacute;a mặt lưng của m&aacute;y gi&uacute;p bạn mở kh&oacute;a ngay lập tức, thao t&aacute;c bằng một tay thuận tiện v&agrave; nhanh ch&oacute;ng.</p>\r\n\r\n<p dir=\"ltr\">Trọng lượng của m&aacute;y khoảng 237g cho bạn cảm gi&aacute;c cứng c&aacute;p, ho&agrave;n to&agrave;n vừa vặn trong l&ograve;ng b&agrave;n tay của bạn. B&ecirc;n cạnh đ&oacute;, Nokia C30 c&ograve;n ho&agrave;n thiện với vỏ ngo&agrave;i được l&agrave;m từ chất liệu polycarbonate cao cấp đem đến sự chắc chắn, bền bỉ với khả năng chống chọi lại c&aacute;c va đập từ b&ecirc;n ngo&agrave;i một c&aacute;ch hiệu quả.</p>\r\n\r\n<p dir=\"ltr\">Về m&agrave;n h&igrave;nh, Nokia C30 sở hữu k&iacute;ch thước 6.82 inch với tỷ lệ khung h&igrave;nh 20:9 gi&uacute;p tối ưu h&oacute;a trải nghiệm của bạn từ học tập, l&agrave;m việc đến xem phim, chơi game giải tr&iacute;. Khả năng hiển thị của d&ograve;ng m&aacute;y n&agrave;y cũng được đ&aacute;nh gi&aacute; tương đối ổn định, h&agrave;i h&ograve;a d&ugrave; sở hữu ấm nền IPS LCD hỗ trợ độ ph&acirc;n giải HD+ kh&ocirc;ng qu&aacute; cao.</p>\r\n\r\n<p dir=\"ltr\"><img src=\"https://lh3.googleusercontent.com/3OLseGhAw5e9HhrMotO43EYlEpYrWSZ0v4IagUtCAmqzMaHsHpk0loTTjSSrHrqvR7cA2pGtnZGT_XSKF6cOLF4GEMLLpM6HLO85rGCNAd6WMbsGGWMJBfycfP8zJ5O0XIYT3gRQ=s0\" style=\"height:336px; width:602px\" /></p>\r\n', 0, '625cdd3055fd6.jpg,625cdd30563be.jpg,625cdd30567a6.jpg', 2290000),
(146, 6, 'Nokia C20', '<p>+ Không gian hiển thị tối đa - Màn hình IPS LCD cực lớn 6.82\", HD+\r\n</p>\r\n<p>+ Hệ điều hành ổn định, mượt mà hơn - Hỗ trợ Android 11 (phiên bản Go Edition) nhiều tiện ích</p>\r\n<p>+ Hiệu năng gaming chuyên biệt - Chip Mediatek G25 cùng 4GB RAM</p>', '<h2><strong>Đ&aacute;nh gi&aacute; Nokia C20 - Smartphone gi&aacute; rẻ, nhiều t&ugrave;y chọn m&agrave;u sắc</strong></h2>\r\n\r\n<p>B&ecirc;n cạnh những chiếc smartphone X10, X20, G10 v&agrave; G20 th&igrave; Nokia lại chuẩn bị đ&oacute;ng g&oacute;p v&agrave;o bộ sưu tập điện thoại th&ocirc;ng minh gi&aacute; rẻ của m&igrave;nh bằng sản phẩm mang t&ecirc;n&nbsp;<strong>Nokia C20</strong>. Với những trang bị c&ugrave;ng t&iacute;nh năng độc đ&aacute;o&nbsp;<a href=\"https://cellphones.com.vn/mobile/hang-sap-ve/nokia.html\" target=\"_self\" title=\"Điện thoại Nokia chính hãng\">điện thoại&nbsp;Nokia</a>&nbsp;n&agrave;y sẽ mang đến cho người d&ugrave;ng những trải nghiệm v&ocirc; c&ugrave;ng ấn tượng v&agrave; độc đ&aacute;o.</p>\r\n\r\n<h3><strong>Thiết kế gọn nhẹ, trọng lượng 191 gam</strong></h3>\r\n\r\n<p>Nokia C20 sở hữu thiết kế gọn nhẹ tương tự như c&aacute;c thế hệ trước đ&oacute; của h&atilde;ng. Vỏ b&ecirc;n ngo&agrave;i l&agrave;m bằng chất liệu nhựa cao cấp mang đến độ bền bỉ vượt trội với thời gian. Từng chi tiết được l&agrave;m kh&aacute; tỉ mỉ v&agrave; chi tiết gi&uacute;p người d&ugrave;ng thay đổi &aacute;nh nh&igrave;n về thiết kế của c&aacute;c sản phẩm gi&aacute; rẻ.</p>\r\n\r\n<p><img alt=\"Thiết kế gọn nhẹ, trọng lượng 191 gam\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/nokia/nokia-c20-1.jpg\" /></p>\r\n\r\n<p>M&aacute;y c&oacute; k&iacute;ch thước 169.90 x 77.90 x 8.80mm c&ugrave;ng với trọng lượng tương đối nhẹ chỉ 191 gam gi&uacute;p việc cầm nắm được trở n&ecirc;n nhẹ nh&agrave;ng v&agrave; tiện lợi hơn trong khi sử dụng. C20 c&oacute; hai phi&ecirc;n bản m&agrave;u sắc cho c&aacute;c bạn lựa chọn đ&oacute; l&agrave; m&agrave;u xanh đậm v&agrave; c&aacute;t v&agrave;ng.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh IPS LCD 6.52 inches, độ ph&acirc;n giải 720x1600 pixels</strong></h3>\r\n\r\n<p>Nokia C20 mang đến cho người d&ugrave;ng trải nghiệm v&ocirc; c&ugrave;ng đ&atilde; với m&agrave;n h&igrave;nh k&iacute;ch thước 6.52 inches, tỉ lệ khung h&igrave;nh 20:9 gi&uacute;p mở rộng th&ecirc;m kh&ocirc;ng gian hiển thị. Từ đ&oacute; mang đến g&oacute;c nh&igrave;n rộng hơn, cảm nhận khi lướt web, xem phim trở n&ecirc;n th&iacute;ch th&uacute; hơn.</p>\r\n\r\n<p><img alt=\"Màn hình IPS LCD 6.52 inches, độ phân giải 720x1600 pixels\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/nokia/nokia-c20-2.jpg\" /></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; h&atilde;ng c&ograve;n trang bị cho m&aacute;y tấm nền IPS LCD, độ ph&acirc;n giải 720x1600 pixels gi&uacute;p hiển thị m&agrave;u sắc trở n&ecirc;n r&otilde; r&agrave;ng hơn. G&oacute;c nh&igrave;n tốt hơn mang đến khả năng hiển thị sắc n&eacute;t với gam m&agrave;u rộng hơn. Với từng đ&oacute; trang bị đủ để người d&ugrave;ng c&oacute; những thước phim hay những m&agrave;n game tuyệt vời nhất.</p>\r\n', 0, '625cde29d446d.jpg,625cde29d4855.jpg,625cde29d4c3d.jpg', 2240000),
(147, 6, 'Nokia 5.4', '<p>+ Chơi game, duyệt web thả ga - Màn hình đục lỗi 6.39in\r\n</p>\r\n<p>+ Bắt trọn thế giới muôn màu - Camera 4 ống kính 48MP và camera trước 16MP\r\n</p>\r\n<p>+ Thời lượng pin lên đến 2 ngày - Pin lớn 4000mAh, công nghệ pin thích ứng hỗ trợ bởi AI</p>', '<h2><strong>Điện thoại Nokia 5.4 &ndash; Màn hình lớn, hi&ecirc;̣u năng lớn</strong></h2>\r\n\r\n<p>Nokia chắc chắn là m&ocirc;̣t trong những thương hi&ecirc;̣u kh&ocirc;ng còn xa lạ với người dùng Vi&ecirc;̣t Nam nữa phải kh&ocirc;ng nào. Nokia g&acirc;̀n đ&acirc;y hãng vừa tiếp tục viết tiếp c&acirc;u chuyện về một huyền thoại&nbsp;khi cho ra mắt thị trường chi&ecirc;́c&nbsp;<strong>Nokia 5.4</strong>&nbsp;với nhi&ecirc;̀u n&acirc;ng c&acirc;́p cùng c&acirc;́u hình mạnh mẽ. Hứa hẹn sẽ làm hài lòng t&acirc;́t cả những người sử dụng, ngay cả những người khó tính nh&acirc;́t.</p>\r\n\r\n<h3><strong>Thi&ecirc;́t k&ecirc;́ nguy&ecirc;n kh&ocirc;́i b&ecirc;̀n bỉ, màn hình IPS LCD 6.39 inch sắc nét</strong></h3>\r\n\r\n<p>Mặc dù là m&ocirc;̣t chi&ecirc;́c smartphone được sản xu&acirc;́t cho ph&acirc;n khúc thị trường t&acirc;̀m trung nhưng&nbsp;<a href=\"https://cellphones.com.vn/mobile/nokia.html\" target=\"_self\" title=\"Điện thoại Nokia chính hãng\">điện thoại Nokia</a>&nbsp;5.4 v&acirc;̃n mang trong mình thi&ecirc;́t k&ecirc;́ kh&ocirc;ng h&ecirc;̀ l&ocirc;̃i thời và kém sang chút nào. Sở hữu thi&ecirc;́t k&ecirc;́ lấy cảm ứng từ Phần Lan, mặt lưng làm từ nhựa phủ bóng giả kính v&ocirc; cùng sang trọng và bắt mắt.</p>\r\n\r\n<p>Máy có kích thước v&ocirc; cùng nhỏ gọn cho người dùng d&ecirc;̃ dàng c&acirc;̀m nắm khi sử dụng. Ngoài ra nhà sản xu&acirc;́t còn cho ra mắt 2 phi&ecirc;n bản m&agrave;u xanh thi&ecirc;n h&agrave; v&agrave; t&iacute;m ho&agrave;ng h&ocirc;n cho người dùng th&ecirc;m nhiều sự lựa chọn.</p>\r\n\r\n<p><img alt=\"Thiết kế nguyên khối bền bỉ, màn hình IPS LCD 6.55 icnh sắc nét\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/nokia/nokia-54-1.png\" /></p>\r\n\r\n<p>Màn hình tr&ecirc;n chi&ecirc;́c Nokia 5.4 có kích thước cực kì r&ocirc;̣ng cho người dùng kh&ocirc;ng gian xem phim, chơi game v&ocirc; cùng thoải mái. Kh&ocirc;ng th&ecirc;̉ tin rằng tại ph&acirc;n khúc t&acirc;̀m trung giá m&ecirc;̀m lại có m&ocirc;̣t chi&ecirc;́c smartphone sở hữu màn hình tới 6.39 inches,</p>\r\n\r\n<p>Ngoài ra còn được tích hợp c&ocirc;ng ngh&ecirc;̣ IPS LCD với tỉ lệ m&agrave;n h&igrave;nh 19.5:9 gi&uacute;p n&acirc;ng cao trải nghi&ecirc;̣m khi sử dụng. Đ&ocirc;̣ ph&acirc;n giải 720 x 1560 pixes, độ s&aacute;ng 400 nits hình ảnh sắc nét.</p>\r\n\r\n<h3><strong>Hi&ecirc;̣u năng mạnh mẽ với CPU Snapdragon 662, Ram 4GB, GPU Adreno 610</strong></h3>\r\n\r\n<p>Kh&ocirc;ng chỉ &acirc;́n tượng từ ngoại hình, màn hình mà hi&ecirc;̣u năng của Nokia 54 thực sự vượt tr&ocirc;̣i khi mang trong mình con chip Snapdragon 662&nbsp;được sản xuất tr&ecirc;n tiến tr&igrave;nh 11nm t&ocirc;́c đ&ocirc;̣ (4x2,0 GHz Kryo 260 Gold &amp; 4x1,8 GHz Kryo 260 Silver) cho hi&ecirc;̣u năng được cải thi&ecirc;̣n m&ocirc;̣t cách đáng k&ecirc;̉.</p>\r\n\r\n<p>M&aacute;y với GPU Adreno 610 kh&ocirc;ng h&ecirc;̀ thua kém gì so với các sản ph&acirc;̉m cao c&acirc;́p gi&uacute;p bạn trải nghiệm c&aacute;c tựa game bom t&acirc;́n hi&ecirc;̣n nay m&ocirc;̣t cách mượt mà và &ocirc;̉n định, ngoài ra bạn còn có th&ecirc;̉ chỉnh ảnh ở mức nhẹ mà kh&ocirc;ng gặp khó khăn gì.</p>\r\n\r\n<p><img alt=\"Hiệu năng mạnh mẽ với CPU Snapdragon 665, Ram 6GB, GPU Adreno 610\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/nokia/nokia-54-2.png\" /></p>\r\n\r\n<p>Dung lượng ram lớn, l&ecirc;n đến 4GB RAM gi&uacute;p người dùng thoải mái sử dụng các tác vụ bình thường hằng ngày hay đ&ecirc;́n vi&ecirc;̣c chi&ecirc;́n các game hot hi&ecirc;̣n nay. B&ocirc;̣ nhớ trong với dung lượng 128GB cho người dùng thoải mái lưu trữ hình ảnh dữ li&ecirc;̣u ứng dụng mà kh&ocirc;ng lo đ&acirc;̀y b&ocirc;̣ nhớ.</p>\r\n', 0, '625ce0013e1ef.jpg,625ce0013e5d7.jpg,625ce0013e9bf.jpg', 3900000),
(148, 6, 'Nokia G21', '<p>+ Chơi game, duyệt web thả ga - Màn hình đục lỗi 6.39in\r\n</p>\r\n<p>+ Bắt trọn thế giới muôn màu - Camera 4 ống kính 48MP và camera trước 16MP\r\n</p>\r\n<p>+ Thời lượng pin lên đến 2 ngày - Pin lớn 4000mAh, công nghệ pin thích ứng hỗ trợ bởi AI</p>', '<h2><strong>Đi&ecirc;̣n thoại Nokia G21 - C&acirc;́u hình tuy&ecirc;̣t vời dành cho mọi người</strong></h2>\r\n\r\n<p>Dù nằm trong ph&acirc;n khúc ph&ocirc;̉ th&ocirc;ng,&nbsp;<strong>Nokia G21</strong>&nbsp;sở hữu loạt th&ocirc;ng s&ocirc;́ &acirc;́n tượng như camera 50 MP, màn hình lớn sắc nét, vi xử lý t&ocirc;́t cùng pin &quot;tr&acirc;u&quot; giúp cho đ&acirc;y là sản ph&acirc;̉m smartphone d&ecirc;̃ ti&ecirc;́p c&acirc;̣n và phù hợp cho t&acirc;́t cả người dùng c&ocirc;ng ngh&ecirc;̣.</p>\r\n\r\n<h3><strong>Thi&ecirc;́t k&ecirc;́ đẹp tinh t&ecirc;́, màn hình 6.5 inch quan sát sắc nét</strong></h3>\r\n\r\n<p>Nokia G21 mang vẻ ngoài tinh t&ecirc;́ với ng&ocirc;n ngữ thi&ecirc;́t k&ecirc;́ đ&acirc;̣m ch&acirc;́t Ph&acirc;̀n Lan đặc trưng của hãng. Lớp vỏ máy được ch&ecirc;́ tạo từ v&acirc;̣t li&ecirc;̣u vừa b&ecirc;̀n bỉ, vừa đạt trọng lượng nhẹ giúp cho máy linh hoạt khi c&acirc;̀m tr&ecirc;n tay. Mặt lưng bo cong với nhi&ecirc;̀u tùy chọn màu sắc giúp điện thoại toát l&ecirc;n nét đẹp bắt mắt tr&ecirc;n tay bạn.</p>\r\n\r\n<p><img alt=\"Thiết kế đẹp tinh tế, màn hình 6.5 inch quan sát sắc nét\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/nokia/nokia-g21-1.jpg\" /></p>\r\n\r\n<p>Đi&ecirc;̣n thoại Nokia G21 giúp bạn giải trí với hình ảnh &acirc;́n tượng và sắc nét. Màn hình 6.5 inch đ&ocirc;̣ ph&acirc;n giải 1600 x 720 (HD Plus) cùng n&ecirc;̀n LCD giúp gia tăng đ&ocirc;̣ nét l&acirc;̃n màu đa dạng cho hình ảnh. Tỷ l&ecirc;̣ khung hình 20:9 hoàn toàn phù hợp đ&ecirc;̉ bạn giải trí xem phim và gaming thỏa thích tr&ecirc;n chiếc G21.</p>\r\n\r\n<h3><strong>Ba camera si&ecirc;u nét đ&ecirc;́n 50 MP chụp trọn cảnh quan</strong></h3>\r\n\r\n<p><strong>Nokia G21</strong>&nbsp;n&acirc;ng c&acirc;́p khả năng chụp ảnh toàn di&ecirc;̣n so với các model ti&ecirc;̀n nhi&ecirc;̣m. Camera chính của điện thoại có đ&ocirc;̣ ph&acirc;n giải t&acirc;̣n 50 MP cung c&acirc;́p cho bạn tính năng chụp ảnh phong cảnh đ&acirc;̀y chi ti&ecirc;́t và rõ nét ch&acirc;n thực, ngay cả ở m&ocirc;i trường ban đ&ecirc;m hoặc thi&ecirc;́u sáng. H&ocirc;̃ trợ cho camera 50 MP tr&ecirc;n còn có hai &ocirc;́ng kính phụ 2MP + 2MP cung c&acirc;́p tính năng chụp ch&acirc;n dung và macro hi&ecirc;̣n đại.</p>\r\n\r\n<p><img alt=\"Ba camera siêu nét đến 50 MP chụp trọn cảnh quan\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/nokia/nokia-g21-2.jpg\" /></p>\r\n\r\n<p>Mặt trước của đi&ecirc;̣n thoại Nokia G21 sẽ còn có camera đ&ocirc;̣ ph&acirc;n giải 8 MP cho tính năng selfie đa dạng. Hơn nữa, đ&ocirc;̣ ph&acirc;n giải 8 MP cũng r&acirc;́t thích hợp đ&ecirc;̉ bạn thực hi&ecirc;̣n video call với người th&acirc;n, và h&ocirc;̃ trợ vi&ecirc;̣c học trực tuy&ecirc;́n m&ocirc;̣t cách ti&ecirc;̣n dụng ngay tr&ecirc;n chi&ecirc;́c smartphone Nokia n&agrave;y.</p>\r\n', 0, '625ce246336f4.jpg,625ce24633adc.jpg,625ce24633ec4.jpg', 3990000),
(149, 6, 'Nokia 2.4', '<p>+ Hiển thị nội dung đã mắt- Màn hình lớn 6.5 inch, độ phân giải DH+ rõ nét\r\n</p>\r\n<p>+ Chụp ảnh chi tiết - Camera kép thông minh, hỗ trợ chế độ chụp đêm\r\n</p>\r\n<p>+ Sử dụng thả ga không lo hết pin - Pin 4500mAh, công nghệ pin thích ứng hỗ trợ bởi AI</p', '<h2><strong>Điện thoại&nbsp;</strong><strong>Nokia 2.4 &ndash;&nbsp;</strong><strong>Điện thoại gi&aacute; rẻ nhiều m&agrave;u sắc</strong></h2>\r\n\r\n<p>L&agrave; một trong những sản phẩm nổi bật trong ph&acirc;n kh&uacute;c tầm trung so với c&aacute;c sản phẩm kh&aacute;c.&nbsp;<strong>Nokia 2.4</strong>&nbsp;mang đến 1 c&aacute;ch nh&igrave;n ho&agrave;n to&agrave;n mới cho người d&ugrave;ng cả về ngoại h&igrave;nh lẫn chất lượng b&ecirc;n trong. Với ph&acirc;n kh&uacute;c tầm trung gi&aacute; rẻ chiếc Nokia 2.4 sẽ mang lại nhiều tiện &iacute;ch v&agrave; chất lượng sử dụng cao cấp d&agrave;nh cho bạn. H&atilde;y c&ugrave;ng CellphoneS t&igrave;m hiểu xem chiếc&nbsp;điện thoại Nokia&nbsp;2.4 n&agrave;y đang sở hữu những g&igrave; b&ecirc;n trong nh&eacute;!</p>\r\n\r\n<h3><strong>Thiết kế 3D kết cấu nano k&iacute;ch thước vừa vặn dễ cầm, nhiều m&agrave;u sắc lựa chọn</strong></h3>\r\n\r\n<p>So với c&aacute;c phi&ecirc;n bản Nokia thế hệ trước chiếc Nokia 2.4 đ&atilde; được cải thiện đ&aacute;ng kể về vẻ bề ngo&agrave;i của thiết bị. Cụ thể với thiết kế 3D kết cấu nano c&aacute;c đường n&eacute;t v&agrave; g&oacute;c cạnh của điện thoại tạo n&ecirc;n một vẻ ngo&agrave;i ho&agrave;n to&agrave;n mới v&agrave; sang trọng khi nh&igrave;n từ b&ecirc;n ngo&agrave;i v&agrave;o.</p>\r\n\r\n<p>Với k&iacute;ch thước vừa vặn dễ cầm chỉ 165,85 mm x 76,3mm Nokia 2.4 mang đến một k&iacute;ch thước chuẩn dễ cầm nắm hay cho v&agrave;o b&ecirc;n trong t&uacute;i quần của người d&ugrave;ng m&agrave; kh&ocirc;ng bị vướng.</p>\r\n\r\n<p><img alt=\"Thiết kế 3D kết cấu nano kích thước vừa vặn dễ cầm\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/nokia/nokia-24-1.jpg\" /></p>\r\n\r\n<p>Kh&ocirc;ng những vậy nhằm đem đến nhiều sự lựa chọn cho kh&aacute;ch h&agrave;ng h&atilde;ng đ&atilde; trang bị cho chiếc Nokia 2.4 n&agrave;y rất nhiều m&agrave;u sắc như m&agrave;u xanh lục lam, m&agrave;u c&aacute;t v&agrave; m&agrave;u than. Người d&ugrave;ng c&oacute; thể dễ d&agrave;ng lựa chọn m&agrave;u sắc tr&ecirc;n chiếc Nokia 2.4 để ph&ugrave; hợp với sở th&iacute;ch v&agrave; c&aacute; t&iacute;nh của m&igrave;nh khi sử dụng. Kh&ocirc;ng bị giới hạn lựa chọn m&agrave;u sắc khi chọn mua Nokia 2.4.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh giọt nước 6.5 inch, cấu h&igrave;nh mượt m&agrave; ổn định</strong></h3>\r\n\r\n<p>Nhằm tạo n&ecirc;n sự nổi bật v&agrave; dễ nh&igrave;n tr&ecirc;n m&agrave;n h&igrave;nh Nokia 2.4 h&atilde;ng đ&atilde; trang bị cho chiếc điện thoại n&agrave;y m&agrave;n h&igrave;nh giọt nước c&oacute; k&iacute;ch thước l&ecirc;n đến 6.5 inch. Chất lượng độ ph&acirc;n giải của chiếc m&agrave;n h&igrave;nh n&agrave;y cũng nằm ở mức HD+ cho mọi h&igrave;nh ảnh hiển thị sắc n&eacute;t đến từng chi tiết.</p>\r\n\r\n<p>Tỷ lệ m&agrave;n h&igrave;nh giữ ở khung 19:9 mang đến một c&aacute;i nh&igrave;n ho&agrave;n to&agrave;n mới v&agrave; c&acirc;n đối tr&ecirc;n Nokia 2.4.</p>\r\n\r\n<p><img alt=\"Màn hình giọt nước 6.5 inch\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/nokia/nokia-24-2.jpg\" /></p>\r\n\r\n<p>Cấu h&igrave;nh tr&ecirc;n chiếc điện thoại Nokia 2.4 được h&atilde;ng tối ưu nhất hiệu suất mang đến một tốc độ xử l&yacute; mượt m&agrave; v&agrave; ổn định. Cụ thể m&aacute;y được trang bị chip CPU Octa-Core MediaTek Helio P22 với tốc độ xử l&yacute; nhanh k&egrave;m bộ nhớ ram l&ecirc;n đến 2Gb cho khả năng đa nhiệm tốt. Ngo&agrave;i ra m&aacute;y cũng sử dụng hệ điều h&agrave;nh Android 10 được tối ưu nhiều chức năng v&agrave; hỗ trợ nhiều chế độ sử dụng tr&ecirc;n Nokia 2.4.</p>\r\n', 0, '625ce38e5e58e.jpg,625ce38e5e976.jpg,625ce38e5ed5e.jpg', 2690000),
(150, 87, 'Xiaomi Redmi Note 11', '<p>+ Nâng cao trải nghiệm thị giác - Màn hình AMOLED 6.43\"\" Full HD+, công nghệ DotDisplay\r\n</p>\r\n<p>+ Hiệu năng ấn tượng - Snapdragon 680 8 nhân cùng RAM 4GB, bộ nhớ 64GB\r\n</p>\r\n<p>+ Chinh phục mọi khoảnh khắc - Cụm 4 camera 50MP, nhiều chế độ chụp tiện ', '<h2><strong>Điện thoại Redmi Note 11 - Lựa chọn h&agrave;ng đầu ph&acirc;n kh&uacute;c gi&aacute; rẻ</strong></h2>\r\n\r\n<p>Nếu như Redmi Note 10 l&agrave; một trong những điện thoại tốt nhất ở thời điểm hiện tại với camera tuyệt vời, thiết kế cao cấp c&ugrave;ng mức gi&aacute; kh&aacute; ấn tượng th&igrave; người d&ugrave;ng lại hi vọng v&agrave;o phi&ecirc;n bản mới mang t&ecirc;n&nbsp;<strong>Redmi Note 11</strong>&nbsp;với những n&acirc;ng cấp nổi bật. Chắc chắn những th&ocirc;ng số xung quanh mẫu điện thoại Xiaomi mới n&agrave;y vẫn đang l&agrave; một ẩn số đối với người h&acirc;m mộ.</p>\r\n\r\n<p>Tham khảo ngay&nbsp;<a href=\"https://cellphones.com.vn/xiaomi-redmi-note-11-pro-plus.html\">Redmi Note 11 Pro Plus ch&iacute;nh h&atilde;ng</a>&nbsp;với mức gi&aacute; hấp dẫn được b&aacute;n độc quyền tại hệ thống CellphoneS.</p>\r\n\r\n<h3><strong>Thiết kế mỏng nhẹ, m&agrave;u sắc thanh lịch</strong></h3>\r\n\r\n<p>Điện thoại Xiaomi Redmi Note 11 kho&aacute;c tr&ecirc;n m&igrave;nh thiết kế v&ocirc; c&ugrave;ng thanh lịch v&agrave; sang trọng. M&aacute;y c&oacute; k&iacute;ch thước nhỏ gọn c&ugrave;ng trọng lượng chỉ kh&aacute; nhẹ gi&uacute;p người d&ugrave;ng thoải m&aacute;i cầm nắm cũng như thao t&aacute;c tr&ecirc;n tay.</p>\r\n\r\n<p><img alt=\"Thiết kế mỏng manh, thanh lịch, chống nước vượt trội với IP68\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/redmi-note-11-3.jpg\" /></p>\r\n\r\n<p>B&ecirc;n cạnh khung viền nguy&ecirc;n khối được ho&agrave;n thiện bằng chất liệu cao cấp th&igrave; mặt trước của Xiaomi Redmi Note 11 được trang bị k&iacute;nh cường lực gi&uacute;p bảo vệ điện thoại an to&agrave;n hơn trước những va chạm khi sử dụng.</p>\r\n\r\n<p>Điện thoại c&oacute; khả năng chống nước v&agrave; bụi bẩn vượt trội với chỉ số IP53. Do đ&oacute;, bạn ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m khi sử dụng dưới trời mưa hoặc kh&ocirc;ng c&ograve;n lo lắng trong trường hợp chẳng may đ&aacute;nh rơi v&agrave;o nước.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh 6.43 inch, khả năng hiển thị 16 triệu m&agrave;u, tần số qu&eacute;t 90Hz</strong></h3>\r\n\r\n<p>Redmi Note 11 được Xiaomi trang bị tấm nền&nbsp;IPS LCD với độ ph&acirc;n phải FHD+ c&ugrave;ng khả năng hiển thị tới 16 triệu m&agrave;u sẽ mang đến chất lượng h&igrave;nh ảnh v&ocirc; c&ugrave;ng sống động. Người d&ugrave;ng c&oacute; thể thoải m&aacute;i tận hưởng v&agrave; trải nghiệm những video giải tr&iacute; hoặc những game đồ hoạ ấn tượng.</p>\r\n\r\n<p><img alt=\"Màn hình 6.47 inch, khả năng hiển thị 16 triệu màu\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/redmi-note-11-2.jpg\" /></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, k&iacute;ch thước 6.43 inch c&ugrave;ng thiết kế tr&agrave;n viền gi&uacute;p mở rộng kh&ocirc;ng gian hiển thị cho người d&ugrave;ng. Tốc độ qu&eacute;t 90Hz mang đến khả năng phản hồi nhanh ch&oacute;ng, thao t&aacute;c vuốt chạm mượt m&agrave; v&agrave; tiết kiệm tối đa điện năng.</p>\r\n\r\n<h3><strong>Mạnh mẽ với chipset&nbsp;Snapdragon 680</strong></h3>\r\n\r\n<p><strong>Xiaomi Redmi Note 11</strong>&nbsp;sở hữu sức mạnh vượt trội với chipset Snapdragon 680. Kết hợp với bộ vi xử l&yacute; l&agrave; dung lượng RAM lớn mang đến tốc độ xử l&yacute; dữ liệu nhanh ch&oacute;ng, ch&iacute;nh x&aacute;c v&agrave; v&ocirc; c&ugrave;ng mạnh mẽ. Bạn ho&agrave;n to&agrave;n c&oacute; thể xử l&yacute; mượt m&agrave; c&aacute;c t&aacute;c vụ đa nhiệm cũng như giải tr&iacute; đỉnh cao với c&aacute;c game đồ hoạ cực hot hiện nay.</p>\r\n\r\n<p><img alt=\"Mạnh mẽ với chipset Qualcomm Snapdragon 865\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/redmi-note-11-1.jpg\" /></p>\r\n\r\n<p>Đ&aacute;p ứng tối đa nhu cầu download v&agrave; lưu trữ dữ liệu của người d&ugrave;ng l&agrave; bộ nhớ trong dung lượng bộ nhớ trong lớn 64GB. Bạn ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m v&agrave; kh&ocirc;ng bị lo ngại về t&igrave;nh trạng đầy bộ nhớ.</p>\r\n', 0, '625ce5089474f.jpg,625ce50894b37.jpg,625ce50894f1f.jpg', 4690000),
(151, 87, 'Xiaomi 11T', '<p>+ Màn hình giải trí cực đỉnh - Màn hình AMOLED 6.67 inch Full HD+, 120Hz\r\n\r\n</p>\r\n<p>+ Cấu hình vượt trội, chuyện động mượt mà - Chip MediaTek Dimensity 1200-Ultra cùng RAM 8GB\r\n</p>\r\n<p>+ Chinh phục mọi khoảnh khắc - Cụm 4 camera 50MP, nhiều chế độ ch', '<p>&nbsp;lẫm với bất kỳ thiết kế điện thoại n&agrave;o của Xiaomi nữa, tuy lần n&agrave;y kh&ocirc;ng d&ugrave;ng thiết kế k&iacute;nh nhưng việc phủ kim loại nguy&ecirc;n khối l&ecirc;n to&agrave;n bộ m&aacute;y cũng gi&uacute;p cho Xiaomi Mi 11T trở n&ecirc;n sang trọng v&agrave; bền bỉ hơn rất nhiều. Điểm kh&aacute;c biệt đ&aacute;ng ch&uacute; &yacute; chỉ nằm ở việc sắp xếp cụm camera sao cho hợp l&yacute; v&agrave; bắt mắt hơn.</p>\r\n\r\n<p><img alt=\"Xiaomi Mi 11T \" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/XIAOMI-11T-1.jpg\" /></p>\r\n\r\n<p>M&agrave;n h&igrave;nh của Xiaomi Mi 11T vẫn thừa hưởng được ở người đ&agrave;n anh ở khả năng hiển thị sống động v&agrave; trải nghiệm những tựa game v&agrave; bộ phim ho&agrave;n hảo. Điều đ&oacute; được thể hiện ở k&iacute;ch thước m&aacute;y ở k&iacute;ch thước 6.67 inhces, đi k&egrave;m l&agrave; chất lượng hiển thị đạt FullHD+ v&agrave; tấm nền Super AMOLED.</p>\r\n\r\n<p>Nhằm gia cố th&ecirc;m cho m&agrave;n h&igrave;nh để chống lại ho&agrave;n to&agrave;n c&aacute;c va đập th&ocirc;ng thường ở b&ecirc;n ngo&agrave;i, chiếc điện thoại Xiaomi 11T c&ograve;n được t&iacute;ch hợp th&ecirc;m một lớp k&iacute;nh cường lực Corning Gorilla Glass Victus. Theo c&aacute;c nh&agrave; sản xuất th&igrave; n&oacute; cho khả năng chống chọi được với lực va đập ở độ cao l&ecirc;n đến 1.6m m&agrave; vẫn c&ograve;n nguy&ecirc;n vẹn m&agrave;n h&igrave;nh.</p>\r\n\r\n<h3><strong>Cụm ba camera bắt trọn từng khoảnh khắc</strong></h3>\r\n\r\n<p>M&aacute;y với cụm camera ch&iacute;nh với độ ph&acirc;n giải 108MP mang lại h&igrave;nh ảnh chất lượng. B&ecirc;n cạnh đ&oacute;, cụm camera c&ograve;n những hai camera phụ nhằm phục vụ cho c&ocirc;ng t&aacute;c chụp ảnh g&oacute;c rộng l&ecirc;n đến 120 độ d&agrave;nh cho độ ph&acirc;n giải 8MP v&agrave; một camera phụ c&ograve;n lại d&ugrave;ng để chụp ảnh macro với độ ph&acirc;n giải l&ecirc;n đến 5 MP. Qua đ&oacute;, bạn thỏa sức s&aacute;ng tạo h&igrave;nh ảnh để đăng tải th&agrave;nh một cuốn album l&ecirc;n mạng x&atilde; hội.</p>\r\n\r\n<p><img alt=\"Xiaomi Mi 11T \" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/XIAOMI-11T-2.jpg\" /></p>\r\n', 0, '625ce642a971c.jpg,625ce642a9b04.jpg,625ce642a9eec.jpg', 8300000),
(152, 87, 'Xiaomi 12', '<p>+ Cân mọi tác vụ với CPU thế hệ mới - Vi xử lí Snapdragon 8 Gen 1 cùng RAM 8 GB</p>\r\n<p>+ Trải ngiệm giải trí hoàn hảo với màn hình tràn viền sắc nét - Kích thước 6.28 inches, AMOLED</p>\r\n<p>+ Cụm camera đa chức năng, lưu giữ mọi khoảnh khắc</p>', '<h2><strong>Đ&aacute;nh gi&aacute; Xiaomi 12 &ndash; Điện thoại camera chất lượng</strong></h2>\r\n\r\n<p>Nối tiếp th&agrave;nh c&ocirc;ng của Mi 11, h&atilde;ng điện thoại Xiaomi tiếp tục cho ra mắt mẫu sản phẩm kế nhiệm mang t&ecirc;n&nbsp;<strong>Xiaomi 12&nbsp;</strong>với nhiều cải tiến vượt trội so với thế hệ trước. Đ&acirc;y hứa hẹn l&agrave; một flagship mạnh mẽ về nhiều mặt từ hiệu năng, dung lượng pin đến camera.</p>\r\n\r\n<p>Ngo&agrave;i ra, bạn cũng c&oacute; thể tham khảo th&ecirc;m&nbsp;<a href=\"https://cellphones.com.vn/xiaomi-mi-12-pro.html\">điện thoại Xiaomi 12 Pro</a>&nbsp;với cấu h&igrave;nh, camera ấn tượng.</p>\r\n\r\n<h3><strong>Thiết kế sang trọng, m&agrave;n h&igrave;nh tr&agrave;n viền</strong></h3>\r\n\r\n<p>Điện thoại Xiaomi 12 sẽ sở hữu thiết kế kế thừa c&aacute;c ưu điểm từ c&aacute;c d&ograve;ng sản phẩm Xiaomi rất th&agrave;nh c&ocirc;ng trước đ&oacute;. M&aacute;y với viền m&agrave;n h&igrave;nh si&ecirc;u mỏng, khung viền c&ugrave;ng g&oacute;c cạnh được l&agrave;m bo cong một c&aacute;ch nhẹ nh&agrave;ng kh&ocirc;ng chỉ mang lại sự tinh tế m&agrave; c&ograve;n mang đến khả năng cầm nắm v&ocirc; c&ugrave;ng thoải m&aacute;i. Xiaomi Mi 12 cũng sở hữu trọng lượng kh&aacute; nhẹ v&agrave; được ra mắt với 3 phi&ecirc;n bản m&agrave;u sắc: X&aacute;m, xanh v&agrave; t&iacute;m</p>\r\n\r\n<p><img alt=\"Xiaomi 12\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/xiaomi-12-5_1.jpg\" /></p>\r\n\r\n<p>M&aacute;y sở hữu m&agrave;n h&igrave;nh k&iacute;ch thước lớn l&ecirc;n đến 6.28 inch c&ugrave;ng độ ph&acirc;n giải l&ecirc;n đến 2400 x 1080 FHD. Với m&agrave;n h&igrave;nh FHD n&agrave;y, mọi chi tiết đều được hiển thị một c&aacute;ch r&otilde; n&eacute;t. Ngo&agrave;i ra m&agrave;n h&igrave;nh n&agrave;y c&ograve;n được bảo vệ bởi một lớp k&iacute;nh cường lực Corning Gorilla Glass Victus c&ugrave;ng tần số qu&eacute;t l&ecirc;n đến 120Hz.</p>\r\n\r\n<p><img alt=\"hình kích thước lớn lên đến 6,28 inch \" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/xiaomi-12-2.jpg\" /></p>\r\n\r\n<h3><strong>Hiệu năng vượt trội với&nbsp;Snapdragon 8 Gen 1</strong></h3>\r\n\r\n<p>L&agrave; một sản phẩm thuộc ph&acirc;n kh&uacute;c cao cấp n&ecirc;n điện thoại Xiaomi 12 sẽ được trang bị con chip mạnh nhất tại thời điểm. Cụ thể Xiaomi Mi 12 được trang bị bộ vi xử l&yacute; Qualcomm Snapdragon 8 Gen 1 với bộ nhớ RAM 8GB. Với cấu h&igrave;nh n&agrave;y, m&aacute;y c&oacute; thể chiến tốt c&aacute;c tựa game v&agrave; hoạt động c&aacute;c ứng dụng cơ bản một c&aacute;ch mượt m&agrave;.</p>\r\n\r\n<p><img alt=\"Xiaomi 12\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/xiaomi-12-2.png\" /></p>\r\n\r\n<p>Về GPU, Xiaomi 12 được trang bị con chip GPU Qualcomm Adreno c&ugrave;ng hệ điều h&agrave;nh Android 12. Điện thoại cũng sở hữu khả năng lưu trữ tốt với dung lượng bộ nhớ trong lớn l&ecirc;n đến 256GB</p>\r\n', 1, '625d245b3ae84.jpg,625d245b3b26c.jpg,625d245b3b654.jpg', 19000000),
(153, 87, 'Xiaomi POCO X3 Pro', '<p>+ Hiệu năng chuẩn Flagship - Snapdragon 860 8 nhân mạnh mẽ, RAM 6G, bộ nhớ UFS 3 1 truy xuất nhanh</p>\r\n<p>+ Vuốt chạm mượt mà, hiển thị chất lượng - Màn hình 6 67 inch FullHD+ tần số quét 120Hz</p>\r\n<p>+ Sạc nhanh siêu tốc 33W, dung lượng pin lớn 5160', '<h2><strong>Điện thoại Xiaomi POCO X3 Pro</strong><strong>&nbsp;&ndash; Điện thoại th&ocirc;ng minh m&agrave;n h&igrave;nh lớn</strong></h2>\r\n\r\n<p><strong>POCO X3 Pro</strong>&nbsp;l&agrave; sản phẩm mới ra mắt với thiết kế kh&aacute; độc đ&aacute;o. Sản phẩm Pocophone n&agrave;y với cụm camera độc đ&aacute;o, nhiều m&agrave;u sắc lựa chọn c&ugrave;ng một cấu h&igrave;nh mạnh hứa hẹn sẽ mang lại trải nghiệm d&ugrave;ng ấn tượng.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh k&iacute;ch thước lớn, mặt k&iacute;nh Gorilla Glass6</strong></h3>\r\n\r\n<p>M&aacute;y sở hữu thiết kế so với phi&ecirc;n bản POCO X3. M&aacute;y với phiền viền được l&agrave;m cong 2,5D mang lại khả năng cầm nắm thoải m&aacute;i.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh Xiaomi POCO X3 Pro c&oacute; k&iacute;ch thước 6,67 inch với độ ph&acirc;n giải FHD+. M&agrave;n với tỉ lệ 20:9 c&ugrave;ng khung viền mỏng mang lại kh&ocirc;ng gian hiển thị lớn. Với độ s&aacute;ng 386 ppi c&ugrave;ng tỉ lệ tương phản lớn 1500:1 c&ugrave;ng tần số qu&eacute;t lớn l&ecirc;n đến 120Hz mang lại khả năng hiển thị cao, chi tiết r&otilde; r&agrave;ng.</p>\r\n\r\n<p><img alt=\"Màn hình kích thước lớn, mặt kính Gorilla Glass6\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/Other/poco-x3-pro-3.jpg\" /></p>\r\n\r\n<p>Đặc biệt, m&agrave;n h&igrave;nh POCO X3 Pro được trang bị lớp k&iacute;nh cường lực Gorilla Glass 5 bền bỉ trong qu&aacute; tr&igrave;nh sử dụng. M&aacute;y sẽ đoực b&aacute;n ra mới 3 phi&ecirc;n bản m&agrave;u: Phantom Black, Metal Bronze v&agrave; Frost Blue.</p>\r\n\r\n<h3><strong>Hiệu năng mạnh mẽ với chip Snapdragon 860 c&ugrave;ng 8GB RAM</strong></h3>\r\n\r\n<p>Về hiệu năng sử dụng, POCO X3 Pro sở hữu một hiệu năng ổn định với chip Snapdragon 860. Con chip được sản xuất tr&ecirc;n tiến tr&igrave;nh 7 nm c&ugrave;ng 8GB RAM mang đến khả năng xử l&yacute; t&aacute;c vụ nhanh, đa nhiệm mượt. M&aacute;y với dung lượng bộ nhớ trong 256GB mang lại kh&ocirc;ng gian lưu trữ lớn.</p>\r\n\r\n<p><img alt=\"Hiệu năng mạnh mẽ với chip Snapdragon 860 cùng 6GB RAM\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/Other/poco-x3-pro-2_1.jpg\" /></p>\r\n\r\n<p>Về GPU, m&aacute;y sở con chip GPU Qualcomm Adreno 640 gi&uacute;p h&igrave;nh ảnh đồ họa cao. Về phần mềm,&nbsp;<strong>Xiaomi POCO X3 Pro</strong>&nbsp;sẽ được chạy sẵn tr&ecirc;n hệ điều h&agrave;nh Android 11 c&ugrave;ng giao diện MIUI 12.</p>\r\n', 0, '625d253299b64.jpg,625d253299f4c.jpg,625d25329a334.jpg', 11900000);
INSERT INTO `tbl_product` (`id`, `category_id`, `name`, `content`, `descripition`, `product_hot`, `img`, `price`) VALUES
(154, 87, 'Xiaomi Redmi 10C', '<p>+ Hiệu năng ổn định trên từng tác vụ, đa nhiệm mượt mà - Vi xử lí Snapdragon 680, RAM 4 GB</p>\r\n<p>+ Camera độ phân giải cao, hỗ trợ nhiều chế độ chụp - Cảm biến chính 50 MP, độ phân giải cao</p>\r\n<p>+ Cụm camera đa chức năng, lưu giữ mọi khoảnh khắc -', '<h2><strong>Đi&ecirc;̣n thoại Xiaomi Redmi 10C - Hi&ecirc;̣u năng &quot;khủng&quot; trong t&acirc;̀m giá phải chăng</strong></h2>\r\n\r\n<p><em>Xiaomi vừa cho ra mắt th&ecirc;m m&ocirc;̣t lựa chọn smartphone tuy&ecirc;̣t vời trong ph&acirc;n khúc ph&ocirc;̉ th&ocirc;ng:&nbsp;</em><em>đi&ecirc;̣n thoại Xiaomi Redmi 10C với màn hình lớn, hi&ecirc;̣u năng &quot;khủng&quot; kèm thời lượng pin dài sẽ giúp người dùng c&ocirc;ng ngh&ecirc;̣ có được chi&ecirc;́c smartphone thích hợp cho năm 2022.</em></p>\r\n\r\n<h3><strong>Gọn nhẹ, năng đ&ocirc;̣ng và có màn hình lớn</strong></h3>\r\n\r\n<p>Được thi&ecirc;́t k&ecirc;́ cho ph&acirc;n khúc ph&ocirc;̉ th&ocirc;ng, Xiaomi Redmi 10C mang ngoại hình trẻ trung và năng đ&ocirc;̣ng, sẵn sàng đáp ứng mọi thao tác sử dụng thường ngày của bạn. Xiaomi Redmi 10C có thi&ecirc;́t k&ecirc;́ linh hoạt với trọng lượng nhẹ và đ&ocirc;̣ mỏng t&ocirc;́t đ&ecirc;̉ bạn lu&ocirc;n c&acirc;̀m máy thoải mái tr&ecirc;n tay. Mặt lưng Xiaomi Redmi 10C được ch&ecirc;́ tạo với họa ti&ecirc;́t đường v&acirc;n đặc bi&ecirc;̣t giúp thi&ecirc;́t bị ngăn bám v&acirc;n tay hi&ecirc;̣u quả, lưu giữ vẻ đẹp và màu sắc của máy.</p>\r\n\r\n<p><img alt=\"Gọn nhẹ, năng động và có màn hình lớn\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/xiaomi-redmi-10c-2.jpg\" /></p>\r\n\r\n<p>Đi&ecirc;̣n thoại Xiaomi Redmi 10C đáp ứng t&ocirc;́t nhu c&acirc;̀u giải trí với màn hình lớn t&acirc;̣n 6.71 inch cho đ&ocirc;̣ ph&acirc;n giải sắc nét Full HD Plus (2340 x 1080). Thi&ecirc;́t k&ecirc;́ màn hình notch &quot;giọt nước&quot; quen thu&ocirc;̣c giúp màn hình Xiaomi Redmi 10C có tỷ l&ecirc;̣ hi&ecirc;̉n thị lớn, cùng với vi&ecirc;̀n bezel thu hẹp hai b&ecirc;n giúp tạo n&ecirc;n hình ảnh cực ch&acirc;́t lượng, giải trí th&ecirc;m h&acirc;́p d&acirc;̃n.</p>\r\n\r\n<h3><strong>Cụm camera k&eacute;p độ ph&acirc;n giải cao hỗ trợ chụp ảnh chuy&ecirc;n s&acirc;u</strong></h3>\r\n\r\n<p>Đi&ecirc;̣n thoại Xiaomi Redmi 10C được n&acirc;ng c&acirc;́p camera sau t&ocirc;́t hơn đời Redmi 9C trước đó. Cụ th&ecirc;̉, chi&ecirc;́c máy trang bị b&ocirc;̣ hai camera sau, c&ugrave;ng nhiều thuật to&aacute;n th&ocirc;ng minh giúp xử lý ảnh chính xác với m&ocirc;i trường hơn. Camera chính của Xiaomi Redmi 10C có đ&ocirc;̣ ph&acirc;n giải t&acirc;̣n 50MP và được n&acirc;ng c&acirc;́p đáng k&ecirc;̉ v&ecirc;̀ khả năng chụp ảnh m&ocirc;i trường và ảnh thi&ecirc;́u sáng. Camera đo độ s&acirc;u 2MP c&ugrave;ng nhiều t&iacute;nh năng chụp ảnh th&ocirc;ng minh mang lại khả năng nhiếp ảnh chất lượng.</p>\r\n\r\n<p><img alt=\"Cụm camera kép độ phân giải cao hỗ trợ chụp ảnh chuyên sâu\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/xiaomi-redmi-10c-8.png\" /></p>\r\n\r\n<p>Ở mặt trước Xiaomi Redmi 10C, chi&ecirc;́c máy cũng có th&ecirc;m camera selfie 5 MP được h&ocirc;̃ trợ th&ecirc;m tính năng góc r&ocirc;̣ng thích hợp đ&ecirc;̉ selfie cùng nhóm đ&ocirc;ng. Khả năng tích hợp HDR và làm đẹp bằng thu&acirc;̣t toán AI cũng là m&ocirc;̣t đi&ecirc;̉m c&ocirc;̣ng đ&ocirc;́i với camera selfie của Xiaomi Redmi 10C đ&ecirc;̉ giúp n&ocirc;̉i b&acirc;̣t khu&ocirc;n mặt chủ th&ecirc;̉.</p>\r\n', 0, '625d27195f52d.jpg,625d27195f915.jpg,625d27195fcfd.jpg', 3550000),
(155, 87, 'Xiaomi Poco X3 GT', '<p>+ Hiệu năng mạnh mẽ hàng đầu - Chip Dimensity 1100 6nm kết hợp cùng GPU Mali-G77</p>\r\n<p>+ Khả năng nhiếp ảnh ấn tượng - Cụm 3 camera sau 64MP, camera selfie 16MP</p>\r\n<p>+ Cụm camera đa chức năng, lưu giữ mọi khoảnh khắc - Camera chính 50 MP đa dạng c', '<h2><strong>Điện Thoại Xiaomi Poco X3 GT - hiệu năng mạnh mẽ, camera cực đỉnh</strong></h2>\r\n\r\n<p>H&atilde;ng điện thoại POCO đ&atilde; t&aacute;ch th&agrave;nh một d&ograve;ng điện thoại độc lập. Ngay sau đ&oacute;, POCO cho ra đời những d&ograve;ng smartphone&nbsp;chất lượng cao nhưng mang t&ecirc;n&nbsp;<strong>Xiaomi Poco X3 GT</strong>. Mẫu&nbsp;<a href=\"https://cellphones.com.vn/mobile/hang-sap-ve/xiaomi.html\" target=\"_self\" title=\"Điện thoại Xiaomi chính hãng\">điện thoại Xiaomi</a>&nbsp;với hiệu năng mạnh mẽ v&agrave; camera sống động hứa hẹn mang lại trải nghiệm th&uacute; vị cho người d&ugrave;ng.</p>\r\n\r\n<h3><strong>Chip Dimensity 1100 hoạt động mạnh mẽ</strong></h3>\r\n\r\n<p>Điện thoại POCO X3 GT sẽ được trang bị con chip Dimensity 1100, hệ thống con chip n&agrave;y cho ph&eacute;p m&aacute;y hoạt động với hiệu năng mạnh mẽ. Con chip n&agrave;y được sản xuất với tiến tr&igrave;nh 6nm của h&atilde;ng với GPU Mali-G77.</p>\r\n\r\n<p><img alt=\"Chip Dimensity 1100 hoạt động mạnh mẽ\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/Xiaomi-Poco-X3-GT-1.jpg\" /></p>\r\n\r\n<p>K&egrave;m theo đ&oacute; l&agrave; dung lượng RAM c&ugrave;ng bộ nhớ trong lớn mang lại khả năng đa nhiệm thoải m&aacute;i hơn. Đồng thời bạn c&oacute; thể thoải m&aacute;i chụp ảnh, lưu trữ, quay video, chơi game,&hellip; m&agrave; kh&ocirc;ng lo hết dung lượng bộ nhớ.</p>\r\n\r\n<p>Một điều đặc biệt đối với c&aacute;c game thủ, POCO X3 GT cho ph&eacute;p bạn tải về v&agrave; chiến tr&ecirc;n c&aacute;c tựa game hot hiện nay. Tuy nhi&ecirc;n để game c&oacute; thể chạy mượt tốt nhất, bạn chỉ n&ecirc;n thiết lập mức đồ họa ph&ugrave; hợp.</p>\r\n\r\n<h3><strong>Sở hữu thiết kế sang trọng v&agrave; năng lượng pin khủng</strong></h3>\r\n\r\n<p>Với thiết kế cạnh sang trọng, bo g&oacute;c mềm mại gi&uacute;p người d&ugrave;ng cầm nắm thoải m&aacute;i hơn. Một sự kết hợp ho&agrave;n thiện từ trong ra ngo&agrave;i với chất liệu cao cấp tạo n&ecirc;n một vẻ đẹp rất cuốn h&uacute;t. Về m&agrave;u sắc, điện thoại cũng sẽ được trang bị nhiều phi&ecirc;n bản m&agrave;u sắc kh&aacute;c nhau cho người d&ugrave;ng lựa chọn như xanh, đen,&hellip;</p>\r\n\r\n<p>Chiếc điện thoại POCO X3 GT sở hữu m&agrave;n h&igrave;nh lớn, sử dụng nền IPS v&agrave; k&iacute;ch thước l&ecirc;n đến 6.6 inch k&egrave;m theo l&agrave; độ ph&acirc;n giải Full HD+ gi&uacute;p cho g&oacute;c nh&igrave;n rộng r&atilde;i v&agrave; ch&ecirc;m ngưỡng m&agrave;u sắc một c&aacute;ch ch&acirc;n thực hơn. M&aacute;y thiết kế m&agrave;n h&igrave;nh đục lỗ - xu hướng thiết kế của năm nhầm đẩy nhanh xu thế, tăng độ đẹp v&agrave; tinh tế của chiếc smartphone.</p>\r\n\r\n<p><img alt=\"Sở hữu thiết kế sang trọng và năng lượng pin khủng\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/Xiaomi-Poco-X3-GT-2.jpg\" /></p>\r\n\r\n<p>K&egrave;m theo đ&oacute; Xiaomi Poco X3 GT c&oacute; thể hoạt động mạnh mẽ nhờ nguồn cung cấp năng lượng bơi pin 5.000 mAh. Tuy kh&ocirc;ng qu&aacute; lớn nhưng dung lượng pin n&agrave;y cho ph&eacute;p m&aacute;y t&iacute;nh sử dụng với những th&ocirc;ng số tương đối khủng v&agrave; được bố tr&iacute; sạc nhanh c&ocirc;ng suất lớn gi&uacute;p pin sạc nhanh hơn.</p>\r\n', 0, '625d2804d655a.jpg,625d2804d6942.jpg,625d2804d6d2a.jpg', 6290000),
(156, 87, 'Xiaomi Redmi 9A', '<p>+ Thiết kế thời thượng - thân hình unibody mỏng, gọn và có thiết kế 3D độc đáo</p>\r\n<p>+ Màn hình sắc nét - 6.53 inch HD+, IPS LCD cho hình ảnh sống động</p>\r\n<p>+ Cụm camera đa chức năng, lưu giữ mọi khoảnh khắc - Camera chính 50 MP đ', '<h2>Đi&ecirc;̣n thoại Xiaomi Redmi 9A - Chi&ecirc;́c smartphone giá rẻ đáp ứng đủ nhu c&acirc;̀u sử dụng</h2>\r\n\r\n<p><em>Mới đ&acirc;y, hãng smartphone Xiaomi&nbsp;vừa cho ra mắt&nbsp;</em><em><strong>Xiaomi Redmi 9A</strong>&nbsp;c&ugrave;ng với&nbsp;<a href=\"https://cellphones.com.vn/xiaomi-redmi-9.html\" target=\"_blank\">Redmi 9</a>. Trang bị màn hình lớn, pin &quot;khủng&quot; cùng camera kép, Xiaomi Redmi 9A sẽ là chi&ecirc;́c smartphone thu&ocirc;̣c ph&acirc;n khúc ph&ocirc;̉ th&ocirc;ng mà bạn kh&ocirc;ng n&ecirc;n bỏ qua.</em></p>\r\n\r\n<h3>Thi&ecirc;́t k&ecirc;́ thời thượng, màn hình 6.53 inch HD+ sắc nét</h3>\r\n\r\n<p>Xiaomi Redmi 9A có th&acirc;n máy làm từ nhựa, với th&acirc;n hình unibody mỏng, gọn và có thi&ecirc;́t k&ecirc;́ 3D đ&ocirc;̣c đáo. Mặt lưng của máy cũng được khắc logo &quot;Redmi&quot; màu trắng v&ocirc; cùng tinh t&ecirc;́. Redmi 9A có ba phi&ecirc;n bản màu là xám, xanh dương, và xanh lá. Bạn có th&ecirc;̉ lựa chọn phi&ecirc;n bản màu thích hợp với cá tính của bản th&acirc;n. Ngoài ra, jack cắm tai nghe 3.5mm v&acirc;̃n hi&ecirc;̣n di&ecirc;̣n tr&ecirc;n Redmi 9A phục vụ những người dùng tai nghe có d&acirc;y truy&ecirc;̀n th&ocirc;́ng.</p>\r\n\r\n<p><img alt=\"Thiết kế thời thượng, màn hình 6.53 inch HD+ sắc nét\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/xiaomi-redmi-9a-1.jpg\" /></p>\r\n\r\n<p><strong>Xiaomi Redmi 9A</strong>&nbsp;được trang bị màn hình thi&ecirc;́t k&ecirc;́ &quot;giọt nước&quot; với kích thước 6.53 inch. Ngoài ra, màn hình còn có đ&ocirc;̣ ph&acirc;n giải 1600 x 720 pixels (HD+) và sử dụng n&ecirc;̀n màu IPS LCD. Tỷ l&ecirc;̣ hi&ecirc;̉n thị 20:9 cùng thi&ecirc;́t k&ecirc;́ vi&ecirc;̀n mỏng xung quanh sẽ giúp màn hình hi&ecirc;̉n thị t&ocirc;́i đa các chi ti&ecirc;́t hình ảnh, tạo trải nghi&ecirc;̣m xem phim hoặc chơi game s&ocirc;́ng đ&ocirc;̣ng.</p>\r\n\r\n<h3>Vi xử lý MediaTek Helio G25, RAM 2GB, 32GB bộ nhớ trong đáp ứng nhu c&acirc;̀u sử dụng cơ bản</h3>\r\n\r\n<p>Với Xiaomi Redmi 9A, bạn có th&ecirc;̉ thỏa sức chi&ecirc;́n game nhờ vào vi xử lý MediaTek Helio G25 b&ecirc;n trong máy. Helio G25 là b&ocirc;̣ CPU được MediaTek t&ocirc;́i ưu cho vi&ecirc;̣c chạy ứng dụng trò chơi nhờ vào c&ocirc;ng ngh&ecirc;̣ HyperEngine, giúp mang lại hi&ecirc;̣u năng mạnh mẽ, ti&ecirc;́t ki&ecirc;̣m năng lượng pin và giảm đ&ocirc;̣ tr&ecirc;̃ t&ocirc;́i đa khi k&ecirc;́t n&ocirc;́i mạng, đảm bảo bạn lu&ocirc;n có trải nghi&ecirc;̣m gaming t&ocirc;́t nh&acirc;́t tr&ecirc;n Redmi 9A.</p>\r\n\r\n<p><img alt=\"Vi xử lý MediaTek Helio G25, RAM 2GB, 32GB bộ nhớ trong đáp ứng nhu cầu sử dụng cơ bản\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/xiaomi/xiaomi-redmi-9a-2.jpg\" /></p>\r\n\r\n<p>Xiaomi Redmi 9A có b&ocirc;̣ nhớ trong RAM 2GB và dung lượng lưu trữ ROM 32GB đáp ứng đủ nhu c&acirc;̀u sử dụng cơ bản. Redmi 9A hoạt đ&ocirc;̣ng tr&ecirc;n h&ecirc;̣ đi&ecirc;̀u hành Android 10, n&ecirc;̀n tảng giao di&ecirc;̣n MIUI 12 mới nh&acirc;́t cho phép bạn thỏa sức tùy bi&ecirc;́n chi&ecirc;́c smartphone của mình.</p>\r\n', 0, '625d28c3356b1.jpg,625d28c335a99.jpg,625d28c335e81.jpg', 2300000);

